"use strict";(self.webpackChunkdocs_website=self.webpackChunkdocs_website||[]).push([[6406],{32885:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"/2022/06/22/release-1.2.0","metadata":{"permalink":"/zh-CN/blog/2022/06/22/release-1.2.0","editUrl":"https://github.com/apache/inlong-website/edit/master/blog/blog/2022-06-22-release-1.2.0.md","source":"@site/i18n/zh-CN/docusaurus-plugin-content-blog/2022-06-22-release-1.2.0.md","title":"1.2.0 \u7248\u672c\u53d1\u5e03","description":"Apache InLong\uff08\u5e94\u9f99\uff09\u662f\u4e00\u4e2a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002","date":"2022-06-22T00:00:00.000Z","formattedDate":"2022\u5e746\u670822\u65e5","tags":[],"readingTime":5.62,"truncated":false,"authors":[],"frontMatter":{"title":"1.2.0 \u7248\u672c\u53d1\u5e03"},"nextItem":{"title":"InLong Sort ETL \u65b9\u6848\u89e3\u6790","permalink":"/zh-CN/blog/2022/06/16/inlong-sort-etl"}},"content":"Apache InLong\uff08\u5e94\u9f99\uff09\u662f\u4e00\u4e2a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002\\nInLong \u652f\u6301\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002\\n\\n## 1.2.0 \u7248\u672c\u7279\u6027\u603b\u89c8\\n**\u521a\u521a\u53d1\u5e03\u7684 1.2.0-incubating \u7248\u672c\u5173\u95ed\u4e86\u7ea6 410+ \u4e2a issue\uff0c\u5305\u542b 30+ \u4e2a\u7279\u6027\u548c 190+ \u4e2a\u4f18\u5316\u3002**\\n\u4e3b\u8981\u5305\u62ec\u4ee5\u4e0b\u5185\u5bb9\uff1a\\n\\n### \u589e\u5f3a\u7ba1\u63a7\u80fd\u529b\\n- Dashboard \u548c Manager \u589e\u52a0\u96c6\u7fa4\u7ba1\u7406\u80fd\u529b\\n- Dashboard \u4f18\u5316\u6570\u636e\u6d41\u7684\u521b\u5efa\u6d41\u7a0b\\n- Manager \u652f\u6301 MQ \u7684\u63d2\u4ef6\u5316\u6269\u5c55\\n\\n### \u6269\u5c55\u91c7\u96c6\u8282\u70b9\\n- \u652f\u6301\u91c7\u96c6 Pulsar \u4e2d\u7684\u6570\u636e\\n- \u652f\u6301\u91c7\u96c6 MongoDB-CDC \u4e2d\u7684\u6570\u636e\\n- \u652f\u6301\u91c7\u96c6 MySQL-CDC \u4e2d\u7684\u6570\u636e\\n- \u652f\u6301\u91c7\u96c6 Oracle-CDC \u4e2d\u7684\u6570\u636e\\n- \u652f\u6301\u91c7\u96c6 PostgreSQL-CDC \u4e2d\u7684\u6570\u636e\\n- \u652f\u6301\u91c7\u96c6 SQLServer-CDC \u4e2d\u7684\u6570\u636e\\n\\n### \u6269\u5c55\u5199\u5165\u8282\u70b9\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 Kafka\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 HBase\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 PostgreSQL\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 Oracle\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 MySQL\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 TDSQL-PostgreSQL\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 Greenplum\\n- \u652f\u6301\u5c06\u6570\u636e\u5199\u5165 SQLServer\\n\\n### \u652f\u6301\u6570\u636e\u8f6c\u6362\\n- \u652f\u6301\u5b57\u7b26\u4e32\u5207\u5272\\n- \u652f\u6301\u5b57\u7b26\u4e32\u6b63\u5219\u66ff\u6362\\n- \u652f\u6301\u5b57\u7b26\u4e32\u6b63\u5219\u66ff\u6362\u7b2c\u4e00\u4e2a\u5339\u914d\u7684\u503c\\n- \u652f\u6301\u6570\u636e\u8fc7\u6ee4\\n- \u652f\u6301\u6570\u636e\u53bb\u91cd\\n- \u652f\u6301 Regular Join\\n\\n### \u589e\u5f3a\u7cfb\u7edf\u76d1\u63a7\u529f\u80fd\\n- \u652f\u6301\u6570\u636e\u94fe\u8def\u5fc3\u8df3\u7684\u4e0a\u62a5\u548c\u7ba1\u7406\\n\\n### \u5176\u4ed6\u4f18\u5316\\n- \u652f\u6301 DataProxy \u591a\u96c6\u7fa4\u914d\u7f6e\u7684\u4e0b\u53d1\\n- GitHub Action \u68c0\u67e5\u3001\u6d41\u6c34\u7ebf\u4f18\u5316\\n\\n## 1.2.0 \u7248\u672c\u7279\u6027\u4ecb\u7ecd\\n\\n### \u652f\u6301\u591a\u96c6\u7fa4\u7ba1\u7406\\nManager \u589e\u52a0\u4e86\u96c6\u7fa4\u7ba1\u7406\u529f\u80fd\uff0c\u652f\u6301\u591a\u96c6\u7fa4\u914d\u7f6e\uff0c\u89e3\u51b3\u4e86\u53ea\u80fd\u901a\u8fc7\u914d\u7f6e\u6587\u4ef6\u5b9a\u4e49\u4e00\u5957\u96c6\u7fa4\u7684\u9650\u5236\uff0c\u7528\u6237\u53ef\u6839\u636e\u9700\u8981\u5728 Dashboard \u521b\u5efa\u4e0d\u540c\u7c7b\u578b\u7684\u96c6\u7fa4\u3002\\n\\n\u591a\u96c6\u7fa4\u529f\u80fd\u4e3b\u8981\u7531 @healchow\u3001@luchunliang\u3001@leezng \u8bbe\u8ba1\u548c\u5b9e\u73b0\uff0c\u611f\u8c22\u4e09\u4f4d\u8d21\u732e\u8005\u3002\\n\\n### \u589e\u5f3a\u5bf9\u6587\u4ef6\u6570\u636e\u548c MySQL Binlog \u7684\u91c7\u96c6\\n1.2.0 \u7248\u672c\u652f\u6301\u91c7\u96c6\u5b8c\u6574\u7684\u6587\u4ef6\u6570\u636e\uff0c\u540c\u65f6\u4e5f\u652f\u6301\u4ece MySQL \u7684\u6307\u5b9a Binlog \u4f4d\u7f6e\u5f00\u59cb\u91c7\u96c6\u6570\u636e\u3002\u8be5\u90e8\u5206\u5de5\u4f5c\u7531 @Greedyu \u5b8c\u6210\u3002\\n\\n### \u652f\u6301\u6574\u5e93\u8fc1\u79fb\\nSort \u652f\u6301\u5bf9\u6574\u4e2a\u6570\u636e\u5e93\u4e2d\u7684\u6570\u636e\u8fdb\u884c\u8fc1\u79fb\uff0c\u6b64\u7279\u6027\u7531 @EMsnap \u8d21\u732e\u3002\\n\\n### \u652f\u6301\u5199\u5165 Canal \u683c\u5f0f\u7684\u6570\u636e\\n\u652f\u6301\u5411 Kafka \u5199\u5165 Canal \u683c\u5f0f\u7684\u6570\u636e\uff0c\u6b64\u7279\u6027\u7531 @thexiay \u8d21\u732e\u3002\\n\\n### \u4f18\u5316 Manager Client \u4e2d\u7684 HTTP \u8bf7\u6c42\u65b9\u5f0f\\n\u4f18\u5316\u4e86 Manager Client \u4e2d\u6267\u884c HTTP \u8bf7\u6c42\u7684\u65b9\u5f0f\uff0c\u5e76\u4e3a Client \u589e\u52a0\u5355\u5143\u6d4b\u8bd5\uff0c\u5728\u51cf\u5c11\u91cd\u590d\u4ee3\u7801\u7684\u540c\u65f6\uff0c\u964d\u4f4e\u7ef4\u62a4\u6210\u672c\u3002\\n\u6b64\u7279\u6027\u7531\u65b0\u52a0\u5165\u7684\u8d21\u732e\u8005 @leosanqing \u8d21\u732e\u3002\\n\\n### \u652f\u6301\u8fd0\u884c SQL \u811a\u672c\\nSort \u652f\u6301\u8fd0\u884c SQL \u811a\u672c\uff0c\u8be6\u89c1 [INLONG-4405](https://github.com/apache/inlong/issues/4405) \uff0c\u611f\u8c22 @gong \u8d21\u732e\u6b64\u7279\u6027\u3002\\n\\n### \u652f\u6301\u6570\u636e\u94fe\u8def\u5fc3\u8df3\u7684\u4e0a\u62a5\u548c\u7ba1\u7406\\n\u6b64\u7248\u672c\u652f\u6301\u6570\u636e\u5206\u7ec4\u3001\u6570\u636e\u6d41\u53ca\u5e95\u5c42\u7ec4\u4ef6\u7684\u5fc3\u8df3\u4e0a\u62a5\u548c\u7ba1\u7406\uff0c\u662f\u540e\u7eed\u7cfb\u7edf\u5404\u73af\u8282\u7684\u72b6\u6001\u7ba1\u7406\u7684\u524d\u63d0\u3002\u6b64\u7279\u6027\u4e3b\u8981\u7531 @baomingyu\u3001@healchow \u548c @kipshi \u8bbe\u8ba1\u548c\u8d21\u732e\u3002\\n\\n### Manager \u652f\u6301\u521b\u5efa\u591a\u79cd\u6d41\u5411\u7684\u8d44\u6e90\\n1.2.0 \u7248\u672c\u4e2d Manager \u589e\u52a0\u4e86\u5bf9\u90e8\u5206\u5b58\u50a8\u8d44\u6e90\u7684\u521b\u5efa\uff1a\\n\\n- \u521b\u5efa Kafka \u7684 Topic\uff08@woofyzhao \u8d21\u732e\uff09\\n- \u521b\u5efa Iceberg \u7684\u5e93\u548c\u8868\uff08@woofyzhao \u8d21\u732e\uff09\\n- \u521b\u5efa HBase \u7684\u547d\u540d\u7a7a\u95f4\u548c\u8868\uff08@woofyzhao \u8d21\u732e\uff09\\n- \u521b\u5efa ClickHouse \u7684\u5e93\u548c\u8868\uff08@lucaspeng12138 \u8d21\u732e\uff09\\n- \u521b\u5efa Elasticsearch \u7684\u7d22\u5f15\uff08@lucaspeng12138 \u8d21\u732e\uff09\\n- \u521b\u5efa PostgreSQL \u7684\u5e93\u548c\u8868\uff08@baomingyu \u8d21\u732e\uff09\\n\\n### Sort \u652f\u6301\u8f7b\u91cf\u5316\u67b6\u6784\\n1.2.0 \u7248\u672c\u7684 Sort \u505a\u4e86\u5927\u91cf\u91cd\u6784\u548c\u63d0\u5347\uff0c\u901a\u8fc7\u5f15\u5165 Flink-CDC\uff0c\u652f\u6301\u591a\u79cd Extract \u548c Load \u8282\u70b9\uff0c\u540c\u65f6\u4e5f\u652f\u6301\u6570\u636e\u7684\u8f6c\u6362\uff08\u5373 Transform\uff09\u3002\\n\\n\u6b64\u7279\u6027\u5305\u542b\u975e\u5e38\u591a\u7684\u5b50\u7279\u6027\uff0c\u4e3b\u8981\u7684\u5f00\u53d1\u8005\u6709\uff1a@baomingyu\uff0c@EMsnap\uff0c@GanfengTan\uff0c@gong\uff0c@lucaspeng12138\uff0c@LvJiancheng\uff0c@kipshi\uff0c@thexiay\uff0c@woofyzhao\uff0c@yunqingmoswu\uff0c\u611f\u8c22\u5404\u4f4d\u7684\u8d21\u732e\u3002\\n\\n\u66f4\u591a\u7279\u6027\u4fe1\u606f\uff0c\u8bf7\u53c2\u8003\uff1a[InLong Sort ETL \u65b9\u6848\u89e3\u6790](./2022-06-16-inlong-sort-etl.md)\u3002\\n\\n### \u5176\u4ed6\u7279\u6027\u53ca\u95ee\u9898\u4fee\u590d\\n\u76f8\u5173\u5185\u5bb9\u8bf7\u53c2\u8003 [\u7248\u672c\u8bf4\u660e](https://github.com/apache/inlong/blob/master/CHANGES.md) \uff0c\u5176\u4e2d\u8be6\u7ec6\u5217\u51fa\u4e86\u6b64\u7248\u672c\u7684\u7279\u6027\u3001\u63d0\u5347\u548c Bug \u4fee\u590d\u3002\\n\\n## Apache InLong \u540e\u7eed\u89c4\u5212\\n\u540e\u7eed\u7248\u672c\uff0c\u6211\u4eec\u6269\u5c55\u66f4\u591a\u7684\u6570\u636e\u6e90\u7aef\u548c\u5b58\u50a8\u7aef\uff0c\u8986\u76d6\u66f4\u591a\u7684\u4f7f\u7528\u573a\u666f\uff0c\u5e76\u9010\u6b65\u63d0\u5347\u7cfb\u7edf\u7684\u6613\u7528\u6027\u548c\u5065\u58ee\u6027\uff0c\u4e3b\u8981\u5305\u62ec\uff1a\\n- \u5404\u7ec4\u4ef6\u7684\u5fc3\u8df3\u4e0a\u62a5\\n- \u6570\u636e\u94fe\u8def\u7684\u72b6\u6001\u7ba1\u7406\\n- \u5168\u94fe\u8def\u5ba1\u8ba1\u652f\u6301\u5199\u5165 ClickHouse\\n- \u6269\u5c55\u66f4\u591a\u7c7b\u578b\u7684\u91c7\u96c6\u8282\u70b9\u548c\u5b58\u50a8\u8282\u70b9"},{"id":"/2022/06/16/inlong-sort-etl","metadata":{"permalink":"/zh-CN/blog/2022/06/16/inlong-sort-etl","editUrl":"https://github.com/apache/inlong-website/edit/master/blog/blog/2022-06-16-inlong-sort-etl.md","source":"@site/i18n/zh-CN/docusaurus-plugin-content-blog/2022-06-16-inlong-sort-etl.md","title":"InLong Sort ETL \u65b9\u6848\u89e3\u6790","description":"1. \u80cc\u666f","date":"2022-06-16T00:00:00.000Z","formattedDate":"2022\u5e746\u670816\u65e5","tags":[],"readingTime":12.845,"truncated":false,"authors":[],"frontMatter":{"title":"InLong Sort ETL \u65b9\u6848\u89e3\u6790"},"prevItem":{"title":"1.2.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2022/06/22/release-1.2.0"},"nextItem":{"title":"1.1.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2022/04/25/release-1.1.0"}},"content":"## 1. \u80cc\u666f\\n\\n\u968f\u7740 Apache InLong(incubating) \u7684\u7528\u6237\u548c\u5f00\u53d1\u8005\u9010\u6e10\u589e\u591a\uff0c\u66f4\u4e30\u5bcc\u7684\u4f7f\u7528\u573a\u666f\u548c\u4f4e\u6210\u672c\u8fd0\u8425\u8bc9\u6c42\u8d8a\u6765\u8d8a\u5f3a\u70c8\uff0c\u5176\u4e2d\uff0cInLong \u5168\u94fe\u8def\u589e\u52a0 Transform\uff08T\uff09\u7684\u9700\u6c42\u53cd\u9988\u6700\u591a\u3002\u7ecf\u8fc7@yunqingmoswu\u3001@EMsnap\u3001@gong\u3001@thexiay \u793e\u533a\u5f00\u53d1\u8005\u7684\u8c03\u7814\u548c\u8bbe\u8ba1\uff0c\u5b8c\u6210\u4e86\u57fa\u4e8e Flink SQL \u7684 InLong Sort ETL \u65b9\u6848\uff0c\u672c\u6587\u5c06\u8be6\u7ec6\u4ecb\u7ecd\u8be5\u65b9\u6848\u7684\u5b9e\u73b0\u7ec6\u8282\u3002\\n\\n\u9996\u5148\uff0c\u57fa\u4e8e Apache Flink SQL \u4e3b\u8981\u6709\u4ee5\u4e0b\u65b9\u9762\u7684\u8003\u91cf\uff1a\\n\\n-  Flink SQL \u62e5\u6709\u5f3a\u5927\u7684\u8868\u8fbe\u80fd\u529b\u5e26\u6765\u7684\u9ad8\u53ef\u6269\u5c55\u6027\u3001\u7075\u6d3b\u6027\uff0c\u57fa\u672c\u4e0a Flink SQL \u80fd\u652f\u6301\u793e\u533a\u5927\u591a\u6570\u9700\u6c42\u573a\u666f\u3002\u5f53 Flink SQL \u5185\u7f6e\u7684\u51fd\u6570\u4e0d\u6ee1\u8db3\u9700\u6c42\u65f6\uff0c\u6211\u4eec\u8fd8\u53ef\u901a\u8fc7\u5404\u79cdUDF\u6765\u6269\u5c55\u3002\\n-  Flink SQL \u76f8\u6bd4 Flink \u5e95\u5c42 API \u5b9e\u73b0\u5f00\u53d1\u6210\u672c\u66f4\u4f4e\uff0c\u53ea\u6709\u7b2c\u4e00\u6b21\u9700\u8981\u5b9e\u73b0 Flink SQL \u7684\u8f6c\u6362\u903b\u8f91\uff0c\u540e\u7eed\u53ef\u4e13\u6ce8\u4e8e Flink SQL \u80fd\u529b\u672c\u8eab\u7684\u6784\u5efa\uff0c\u6bd4\u5982\u6269\u5c55 Connector\u3001\u81ea\u5b9a\u4e49\u51fd\u6570UDF\u7b49\u3002\\n- \u4e00\u822c\u6765\u8bf4\uff0cFlink SQL \u5c06\u66f4\u5065\u58ee\u3001\u8fd0\u884c\u4e5f\u5c06\u66f4\u7a33\u5b9a\u3002\u539f\u56e0\u5728\u4e8e Flink SQL \u5c4f\u853d\u4e86 Flink \u5e95\u5c42\u5927\u91cf\u7684\u7ec6\u8282\uff0c\u6709\u5f3a\u5927\u7684\u793e\u533a\u652f\u6301\uff0c\u5e76\u4e14\u7ecf\u8fc7\u5927\u91cf\u7528\u6237\u7684\u5b9e\u8df5\u3002\\n- \u5bf9\u7528\u6237\u6765\u8bf4\uff0cFlink SQL \u4e5f\u66f4\u52a0\u901a\u4fd7\u6613\u61c2\uff0c\u7279\u522b\u662f\u5bf9\u4f7f\u7528\u8fc7 SQL \u7528\u6237\u6765\u8bf4\uff0c\u4f7f\u7528\u65b9\u5f0f\u7b80\u5355\u3001\u719f\u6089\uff0c\u8fd9\u6709\u52a9\u4e8e\u7528\u6237\u5feb\u901f\u843d\u5730\u3002\\n- \u5bf9\u4e8e\u5b58\u91cf\u5b9e\u65f6\u4efb\u52a1\u7684\u8fc1\u79fb\uff0c\u5982\u679c\u5176\u539f\u672c\u5c31\u662f SQL \u7c7b\u578b\u7684\u4efb\u52a1\uff0c\u5c24\u5176\u662f Flink SQL \u4efb\u52a1\uff0c\u5176\u8fc1\u79fb\u6210\u672c\u6781\u4f4e\uff0c\u90e8\u5206\u60c5\u51b5\u4e0b\u751a\u81f3\u90fd\u4e0d\u7528\u505a\u4efb\u4f55\u6539\u52a8\u3002\\n\\n\u6ce8\u610f\uff1a\u672c\u65b9\u6848\u7684\u6240\u6709\u4ee3\u7801\uff0c\u53ef\u4ee5\u53c2\u8003 [Apache InLong Sort](https://github.com/apache/incubator-inlong/tree/master/inlong-sort) \u6a21\u5757\uff0c\u6240\u542b\u529f\u80fd\u53ef\u5728\u5373\u5c06\u53d1\u5e03\u7684 1.2.0 \u7248\u672c\u4e2d\u4e0b\u8f7d\u4f7f\u7528\u3002\\n\\n## 2. \u65b9\u6848\u4ecb\u7ecd\\n\\n### 2.1 \u65b9\u6848\u9700\u6c42\\n\\n\u8be5\u65b9\u6848\u7684\u4e3b\u8981\u9700\u6c42\uff0c\u662f\u5b8c\u6210\u7684 InLong Sort \u6a21\u5757 Transform\uff08T\uff09\u80fd\u529b\uff0c\u5305\u62ec\uff1a\\n\\n|  Transform   |                            \u8bf4\u660e                            |\\n| :----------: | :--------------------------------------------------------: |\\n|  \u7a97\u53e3\u5185\u53bb\u91cd  |                 \u5728\u4e00\u4e2a\u65f6\u95f4\u7a97\u53e3\u5185\u5bf9\u6570\u636e\u53bb\u91cd                 |\\n| \u65f6\u95f4\u7a97\u53e3\u805a\u5408 |             \u5728\u4e00\u4e2a\u65f6\u95f4\u7a97\u53e3\u5185\u5bf9\u6570\u636e\u8fdb\u884c\u805a\u5408\u64cd\u4f5c             |\\n| \u65f6\u95f4\u683c\u5f0f\u8f6c\u6362 |          \u5c06\u4e00\u4e2a\u5b57\u6bb5\u7684\u503c\u8f6c\u6362\u4e3a\u76ee\u6807\u65f6\u95f4\u683c\u5f0f\u7684\u5b57\u7b26\u4e32          |\\n|   \u5b57\u6bb5\u5206\u5272   |         \u5c06\u4e00\u4e2a\u5b57\u6bb5\u901a\u8fc7\u67d0\u4e2a\u5206\u5272\u7b26\u5206\u5272\u4e3a\u591a\u4e2a\u65b0\u7684\u5b57\u6bb5         |\\n|  \u5b57\u7b26\u4e32\u66ff\u6362  |           \u5c06\u66ff\u6362\u4e00\u4e2a\u5b57\u7b26\u4e32\u5b57\u6bb5\u4e2d\u7684\u90e8\u5206\u6216\u5168\u90e8\u5185\u5bb9           |\\n|   \u6570\u636e\u8fc7\u6ee4   |              \u5c06\u6ee1\u8db3\u8fc7\u6ee4\u6761\u4ef6\u7684\u6570\u636e\u820d\u5f03\u6216\u8005\u4fdd\u7559              |\\n|   \u5185\u5bb9\u63d0\u53d6   |            \u63d0\u53d6\u4e00\u4e2a\u5b57\u6bb5\u7684\u4e00\u90e8\u5206\u4ea7\u751f\u4e00\u4e2a\u65b0\u7684\u5b57\u6bb5            |\\n|     \u8fde\u63a5     |                       \u652f\u6301\u4e24\u8868 Join                        |\\n|    \u503c\u66ff\u6362    | \u7ed9\u5b9a\u4e00\u4e2a\u5339\u914d\u503c\uff0c\u5982\u679c\u8be5\u5b57\u6bb5\u7684\u503c\u7b49\u4e8e\u8be5\u503c\uff0c\u5219\u5c06\u5176\u66ff\u6362\u4e3a\u76ee\u6807\u503c |\\n\\n### 2.2 \u4f7f\u7528\u573a\u666f\\n\\n\u5927\u6570\u636e\u96c6\u6210\u7684\u7528\u6237\uff0c\u5728\u5f88\u591a\u4e1a\u52a1\u573a\u666f\u4e0b\u90fd\u6709\u6570\u636e\u8f6c\u6362\u3001\u8fde\u63a5\u3001\u8fc7\u6ee4\u7b49 Transform \u9700\u6c42\u3002\\n\\n### 2.3 \u8bbe\u8ba1\u76ee\u6807\\n\\n\u672c\u6b21\u8bbe\u8ba1\u9700\u8981\u8fbe\u5230\u4ee5\u4e0b\u76ee\u6807\uff1a\\n\\n- \u529f\u80fd\u6027\uff1a\u5728 InLong Sort \u73b0\u6709\u67b6\u6784\u3001\u6570\u636e\u6d41\u6a21\u578b\u4e0b\uff0c\u8986\u76d6\u57fa\u672c\u7684 Transform \u80fd\u529b\uff0c\u5e76\u5177\u5907\u5feb\u901f\u6269\u5f20\u7684\u80fd\u529b\u3002\\n- \u517c\u5bb9\u6027\uff1a\u65b0\u7684 InLong Sort \u6570\u636e\u6a21\u578b\u5411\u524d\u517c\u5bb9\uff0c\u786e\u4fdd\u5386\u53f2\u4efb\u52a1\u80fd\u591f\u6b63\u5e38\u914d\u7f6e\u8fd0\u884c\u3002\\n- \u53ef\u7ef4\u62a4\u6027\uff1aInLong Sort \u6570\u636e\u6a21\u578b\u8f6c Flink SQL \u53ea\u9700\u5b9e\u73b0\u4e00\u904d\uff0c\u540e\u671f\u6709\u65b0\u589e\u7684\u529f\u80fd\u9700\u6c42\u65f6\uff0c\u8fd9\u5757\u4e0d\u9700\u8981\u6539\u52a8\uff0c\u54ea\u6015\u6709\u6539\u52a8\u4e5f\u662f\u5c11\u91cf\u6539\u52a8\u5373\u53ef\u652f\u6301\u3002\\n- \u53ef\u6269\u5c55\u6027\uff1a\u5f53\u51fa\u73b0\u5f00\u6e90 Flink Connector \u6216\u8005\u5185\u7f6e Flink SQL \u51fd\u6570\u4e0d\u6ee1\u8db3\u9700\u6c42\u65f6\uff0c\u53ef\u901a\u8fc7\u81ea\u5b9a\u4e49 Flink Connector\u3001UDF \u6765\u5b9e\u73b0\u5176\u529f\u80fd\u6269\u5c55\u3002\\n\\n### 2.4 \u57fa\u672c\u6982\u5ff5\\n\\n\u6838\u5fc3\u6982\u5ff5\u53c2\u7167\u6982\u8981\u8bbe\u8ba1\u4e2d\u7684\u540d\u8bcd\u89e3\u91ca\\n\\n|            \u540d\u79f0             |                             \u542b\u4e49                             |\\n| :-------------------------: | :----------------------------------------------------------: |\\n|      InLong Dashborad       |                     Inlong \u524d\u7aef\u7ba1\u7406\u754c\u9762                      |\\n|    InLong Manager Client    | \u5c06 Manager \u5f53\u4e2d\u7684\u63a5\u53e3\u8fdb\u884c\u5305\u88c5\uff0c\u4f9b\u5916\u90e8\u7528\u6237\u7a0b\u5e8f\u8c03\u7528\uff0c\u4e0d\u7ecf\u8fc7\u524d\u7aef InLong Dashboard |\\n|   InLong Manager Openapi    |               Inlong manager \u4e0e\u5916\u90e8\u7cfb\u7edf\u8c03\u7528\u63a5\u53e3               |\\n|   InLong Manager metaData   | Inlong manager \u5143\u6570\u636e\u7ba1\u7406\uff0c\u5305\u62ec group\u3001stream \u7eac\u5ea6\u7684\u5143\u6570\u636e\u4fe1\u606f |\\n| InLong Manager task manager | Inlong manager\u4e2d\u7ba1\u7406\u6570\u636e\u6e90\u91c7\u96c6\u4efb\u52a1\u6a21\u5757\uff0c\u7ba1\u7406agent\u7684\u4efb\u52a1\u4e0b\u53d1\uff0c\u6307\u4ee4\u4e0b\u53d1\uff0c\u5fc3\u8df3\u4e0a\u62a5 |\\n|        InLong Group         |     \u6570\u636e\u6d41\u7ec4\uff0c\u5305\u542b\u591a\u4e2a\u6570\u636e\u6d41\uff0c\u4e00\u4e2a Group \u4ee3\u8868\u4e00\u4e2a\u6570\u636e\u63a5\u5165     |\\n|        InLong Stream        |                \u6570\u636e\u6d41\uff0c\u4e00\u4e2a\u6570\u636e\u6d41\u6709\u5177\u4f53\u7684\u6d41\u5411                |\\n|        Stream Source        |  \u6d41\u4e2d\u6709\u5bf9\u5e94\u7684\u91c7\u96c6\u7aef\u548c sink \u7aef\uff0c\u672c\u8bbe\u8ba1\u4e2d\u53ea\u6d89\u53ca\u5230 stream source  |\\n|         Stream Info         |  Sort \u4e2d\u6570\u636e\u6d41\u5411\u7684\u62bd\u8c61\uff0c\u5305\u542b\u8be5\u6570\u636e\u6d41\u7684\u5404\u79cd\u6765\u6e90\u3001\u8f6c\u6362\u3001\u53bb\u5411\u7b49  |\\n|         Group Info          |  Sort \u4e2d\u5bf9\u6570\u636e\u6d41\u5411\u7684\u5c01\u88c5\uff0c\u4e00\u4e2a GroupInfo \u53ef\u5305\u542b\u591a\u4e2a Stream Info  |\\n|            Node             |          \u6570\u636e\u540c\u6b65\u4e2d\u6570\u636e\u6e90\u3001\u6570\u636e\u8f6c\u6362\u3001\u6570\u636e\u53bb\u5411\u7684\u62bd\u8c61          |\\n|        Extract Node         |                     \u6570\u636e\u540c\u6b65\u7684\u6765\u6e90\u7aef\u62bd\u8c61                     |\\n|          Load Node          |                     \u6570\u636e\u540c\u6b65\u7684\u53bb\u5411\u7aef\u62bd\u8c61                     |\\n|     MySQL Extract Node      |                      MySQL \u6570\u636e\u6765\u6e90\u62bd\u8c61                      |\\n|       Kafka Load Node       |                      Kafka \u6570\u636e\u53bb\u5411\u62bd\u8c61                      |\\n|       Transform Node        |                    \u6570\u636e\u540c\u6b65\u7684\u8f6c\u6362\u8fc7\u7a0b\u62bd\u8c61                    |\\n|  Aggregate Transform Node   |                  \u6570\u636e\u540c\u6b65\u805a\u5408\u7c7b\u8f6c\u6362\u8fc7\u7a0b\u62bd\u8c61                  |\\n|        Node Relation        |                  \u6570\u636e\u540c\u6b65\u4e2d\u5404\u4e2a\u8282\u70b9\u5173\u7cfb\u62bd\u8c61                  |\\n|       Field Relation        |             \u6570\u636e\u540c\u6b65\u4e2d\u4e0a\u4e0b\u6e38\u8282\u70b9\u5b57\u6bb5\u95f4\u5173\u7cfb\u7684\u62bd\u8c61             |\\n|          Function           |       \u8f6c\u6362\u51fd\u6570\u7684\u62bd\u8c61\uff0c\u5373\u6570\u636e\u540c\u6b65T\u4e2d\u5404\u4e2a T \u80fd\u529b\u5b9e\u73b0\u7684\u62bd\u8c61       |\\n|     Substring Function      |                     \u5b57\u7b26\u4e32\u622a\u53d6\u51fd\u6570\u7684\u62bd\u8c61                     |\\n|       Filter Function       |                      \u6570\u636e\u8fc7\u6ee4\u51fd\u6570\u7684\u62bd\u8c61                      |\\n|       Function Param        |                        \u51fd\u6570\u7684\u5165\u53c2\u62bd\u8c61                        |\\n|       Constant Param        |                           \u5e38\u91cf\u53c2\u6570                           |\\n|         Field Info          |                           \u8282\u70b9\u5b57\u6bb5                           |\\n|       Meta FieldInfo        |                        \u8282\u70b9\u5143\u4fe1\u606f\u5b57\u6bb5                        |\\n\\n### 2.5 \u9886\u57df\u6a21\u578b\\n\\n\u672c\u6b21\u8bbe\u8ba1\u4e3b\u8981\u6d89\u53ca\u5230\u4ee5\u4e0b\u5b9e\u4f53\uff1a\\n\\nGroup\u3001Stream\u3001GroupInfo\u3001StreamInfo\u3001Node\u3001NodeRelation\u3001FieldRelation\u3001Function\u3001FilterFunction\u3001SubstringFunction\u3001FunctionParam\u3001FieldInfo\u3001MetaFieldInfo\u3001MySQLExtractNode\u3001KafkaLoadNode \u7b49\\n\\n\u4e3a\u4e86\u4fbf\u4e8e\u7406\u89e3\uff0c\u672c\u5c0f\u8282\u5c06\u5bf9\u5b9e\u4f53\u4e4b\u95f4\u5173\u7cfb\u8fdb\u884c\u5efa\u6a21\u5206\u6790\u3002\u9886\u57df\u6a21\u578b\u7684\u5b9e\u4f53\u5bf9\u5e94\u5173\u7cfb\u8bf4\u660e\uff1a\\n\\n- \u4e00\u4e2a Group \u5bf9\u5e94 1 \u4e2a GroupInfo\\n- \u4e00\u4e2a Stream \u5bf9\u5e94 1 \u4e2a StreamInfo\\n- \u4e00\u4e2a Group \u5305\u542b 1 \u4e2a\u6216\u591a\u4e2a Stream\\n- \u4e00\u4e2a GroupInfo \u5305\u542b 1 \u4e2a\u6216\u591a\u4e2a StreamInfo\\n- \u4e00\u4e2a StreamInfo \u5305\u542b\u591a\u4e2a Node\\n- \u4e00\u4e2a StreamInfo \u5305\u542b 1 \u4e2a\u6216\u591a\u4e2a NodeRelation\\n- \u4e00\u4e2a NodeRelation \u5305\u542b 1 \u4e2a\u6216\u591a\u4e2a FieldRelation\\n- \u4e00\u4e2a NodeRelation \u5305\u542b 0 \u4e2a\u6216\u591a\u4e2a FilterFunction\\n- \u4e00\u4e2a FieldRelation \u5305\u542b 1 \u4e2a Function \u6216 1 \u4e2a FieldInfo \u4f5c\u4e3a\u6765\u6e90\u5b57\u6bb5\uff0c1 \u4e2a FieldInfo \u4f5c\u4e3a\u76ee\u6807\u5b57\u6bb5\\n- \u4e00\u4e2a Function \u5305\u542b 1 \u4e2a\u6216\u591a\u4e2a FunctionParam\\n\\n\u4e0a\u8ff0\u5173\u7cfb\u7531 UML \u5bf9\u8c61\u5173\u7cfb\u56fe\u53ef\u4ee5\u8868\u793a\u4e3a\uff1a\\n\\n![sort_UML](./img/sort_UML.png)\\n\\n### 2.6 \u529f\u80fd\u7528\u4f8b\u56fe\\n\\n![sort-usecase](./img/sort-usecase.png)\\n\\n## 3. \u7cfb\u7edf\u6982\u8981\u8bbe\u8ba1\\n\\n### 3.1 \u7cfb\u7edf\u67b6\u6784\u56fe\\n\\n![architecture](./img/architecture.png)\\n\\n- Serialization\uff1a\u5e8f\u5217\u5316\u5b9e\u73b0\u6a21\u5757\\n- Deserialization\uff1a\u53cd\u5e8f\u5217\u5316\u5b9e\u73b0\u6a21\u5757\\n- Flink Source\uff1a\u81ea\u5b9a\u4e49 Flink source\u5b9e\u73b0\u6a21\u5757\\n- Flink Sink\uff1a\u81ea\u5b9a\u4e49\u7684 Flink sink \u5b9e\u73b0\u6a21\u5757\\n- Transformation\uff1a\u81ea\u5b9a\u4e49\u7684 Transform \u5b9e\u73b0\u6a21\u5757\\n- GroupInfo\uff1a\u5bf9\u5e94 Inlong group\\n- StreamInfo\uff1a\u5bf9\u5e94 Inlong stream\\n- Node\uff1a\u5bf9\u6570\u636e\u540c\u6b65\u4e2d\u6570\u636e\u6765\u6e90\u3001\u6570\u636e\u8f6c\u6362\u3001\u6570\u636e\u53bb\u5411\u7684\u62bd\u8c61\\n- FlinkSQLParser\uff1aSQL \u89e3\u6790\u5668\\n\\n### 3.2 InLong Sort \u5185\u90e8\u8fd0\u884c\u6d41\u7a0b\u56fe\\n\\n![](./img/sort-operation-flow.png)\\n\\n### 3.3 \u6a21\u5757\u8bbe\u8ba1\\n\\n\u672c\u6b21\u8bbe\u8ba1\u53ea\u5bf9\u539f\u6709\u7cfb\u7edf\u589e\u52a0 Flink Connector\u3001FlinkSQL Generator \u4e24\u4e2a\u6a21\u5757\uff0c\u5bf9 Data Model \u6a21\u5757\u6709\u4fee\u6539\u3002\\n\\n#### 3.3.1 \u6a21\u5757\u7ed3\u6784\\n\\n![](./img/sort-module-structure.png)\\n\\n#### 3.3.2 \u6a21\u5757\u5212\u5206\\n\\n\u91cd\u8981\u6a21\u5757\u5212\u5206\u8bf4\u660e\uff1a\\n\\n|       \u540d\u79f0        |                             \u8bf4\u660e                             |\\n| :---------------: | :----------------------------------------------------------: |\\n|  FlinkSQLParser   |       \u7528\u4e8e\u751f\u6210 FlinkSQL \u6838\u5fc3\u7c7b\uff0c\u5305\u542b GroupInfo \u7684\u5f15\u7528        |\\n|     GroupInfo     | Sort \u5185\u90e8\u5bf9 InlongGroup \u7684\u62bd\u8c61\uff0c\u7528\u4e8e\u5c01\u88c5\u6574\u4e2a InlongGroup \u540c\u6b65\u76f8\u5173\u4fe1\u606f\uff0c\u5305\u542b\u5bf9 List\\\\<StreamInfo\\\\> \u7684\u5f15\u7528 |\\n|    StreamInfo     | Sort \u5185\u90e8\u5bf9 InlongStream \u7684\u62bd\u8c61\uff0c\u7528\u4e8e\u5c01\u88c5 InlongStream \u540c\u6b65\u76f8\u5173\u4fe1\u606f\uff0c\u5305\u542bList\\\\<Node\\\\>\u3001List\\\\<NodeRelation\\\\> \u7684\u5f15\u7528 |\\n|       Node        | \u540c\u6b65\u8282\u70b9\u7684\u9876\u5c42\u63a5\u53e3\uff0c\u5b83\u7684\u5404\u4e2a\u5b50\u7c7b\u5b9e\u73b0\u4e3b\u8981\u7528\u4e8e\u5bf9\u540c\u6b65\u6570\u636e\u6e90\u3001\u8f6c\u6362\u8282\u70b9\u7684\u6570\u636e\u5c01\u88c5 |\\n|    ExtractNode    |               \u6570\u636eextract\u8282\u70b9\u62bd\u8c61\uff0c\u7ee7\u627f\u81ea Node                |\\n|     LoadNode      |                 \u6570\u636eload\u8282\u70b9\u62bd\u8c61\uff0c\u7ee7\u627f\u81ea Node                 |\\n|   TransformNode   |                \u6570\u636e\u8f6c\u6362\u8282\u70b9\u62bd\u8c61\uff0c\u7ee7\u627f\u81ea Node                  |\\n|   NodeRelation    |                       \u5b9a\u4e49\u8282\u70b9\u95f4\u7684\u5173\u7cfb                       |\\n|   FieldRelation   |                     \u5b9a\u4e49\u8282\u70b9\u95f4\u5b57\u6bb5\u7684\u5173\u7cfb                     |\\n|     Function      |                     T\u80fd\u529b\u6267\u884c\u51fd\u6570\u7684\u62bd\u8c61                      |\\n|  FilterFunction   |         \u7528\u4e8e\u6570\u636e\u8fc7\u6ee4\u7684 Function \u62bd\u8c61\uff0c\u7ee7\u627f\u81ea Function         |\\n| SubstringFunction |         \u7528\u4e8e\u5b57\u7b26\u4e32\u622a\u53d6 Function \u62bd\u8c61\uff0c\u7ee7\u627f\u81ea Function         |\\n|   FunctionParam   |                      \u7528\u4e8e\u51fd\u6570\u53c2\u6570\u7684\u62bd\u8c61                      |\\n|   ConstantParam   |           \u51fd\u6570\u5e38\u91cf\u53c2\u6570\u7684\u5c01\u88c5\uff0c\u7ee7\u627f\u81ea FunctionParam            |\\n|     FieldInfo     |   \u8282\u70b9\u5b57\u6bb5\u7684\u5c01\u88c5\uff0c\u4e5f\u53ef\u505a\u51fd\u6570\u5165\u53c2\u4f7f\u7528\uff0c\u7ee7\u627f\u81ea FunctionParam       |\\n|   MetaFieldInfo   | \u5185\u7f6e\u5b57\u6bb5\u7684\u5c01\u88c5\uff0c\u76ee\u524d\u4e3b\u8981\u7528\u4e8e canal-json \u7684\u5143\u6570\u636e\u5b57\u6bb5\u573a\u666f\uff0c\u7ee7\u627f\u81ea FieldInfo |\\n\\n## 4. \u7cfb\u7edf\u8be6\u7ec6\u8bbe\u8ba1\\n\\n\u4e0b\u9762\u4ee5\u540c\u6b65 MySQL \u4e2d\u7684\u6570\u636e\u5230 Kafka \u4e3a\u4f8b\u6765\u8bf4\u660e SQL \u7684\u751f\u6210\u539f\u7406\u3002\\n\\n### 4.1 Node \u751f\u6210 SQL\\n\\n#### 4.1.1 ExtractNode \u751f\u6210 SQL\\n\\n\u8282\u70b9\u914d\u7f6e\u4e3a\uff1a\\n\\n```java\\n private Node buildMySQLExtractNode() {\\n        List<FieldInfo> fields = Arrays.asList(\\n                new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                new FieldInfo(\\"age\\", new IntFormatInfo()));\\n        return new MySqlExtractNode(\\"1\\", \\"mysql_input\\", fields,\\n                null, null, \\"id\\",\\n                Collections.singletonList(\\"tableName\\"), \\"localhost\\", \\"root\\", \\"password\\",\\n                \\"inlong\\", null, null,\\n                null, null);\\n    }\\n```\\n\\n\u751f\u6210\u7684 SQL \u4e3a\uff1a\\n\\n```sql\\nCREATE TABLE `mysql_1` (`name` string,`age` int) \\nwith \\n(\'connector\' = \'mysql-cdc-inlong\',\\n\'hostname\' = \'localhost\',\\n\'username\' = \'root\',\\n\'password\' = \'password\',\\n\'database-name\' = \'inlong\',\\n\'table-name\' = \'tableName\')\\n```\\n\\n#### 4.1.2 TransformNode \u751f\u6210 SQL\\n\\n\u8282\u70b9\u914d\u7f6e\u4e3a\uff1a\\n\\n```java\\n List<FilterFunction> filters = Arrays.asList(\\n                new SingleValueFilterFunction(EmptyOperator.getInstance(),\\n                        new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                        LessThanOperator.getInstance(), new ConstantParam(25)),\\n                new SingleValueFilterFunction(AndOperator.getInstance(),\\n                        new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                        MoreThanOrEqualOperator.getInstance(), new ConstantParam(18))\\n        );\\n```\\n\\n\u751f\u6210\u7684 SQL \u4e3a\uff1a\\n\\n```sql\\nSELECT `name` AS `name`,`age` AS `age` FROM `mysql_1` WHERE `age` < 25 AND `age` >= 18\\n```\\n\\n#### 4.1.3 LoadNode \u751f\u6210 SQL\\n\\n\u8282\u70b9\u914d\u7f6e\u4e3a\uff1a\\n\\n```java\\n private Node buildKafkaLoadNode(FilterStrategy filterStrategy) {\\n        List<FieldInfo> fields = Arrays.asList(\\n                new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                new FieldInfo(\\"age\\", new IntFormatInfo())\\n        );\\n        List<FieldRelation> relations = Arrays\\n                .asList(\\n                        new FieldRelation(new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                                new FieldInfo(\\"name\\", new StringFormatInfo())),\\n                        new FieldRelation(new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                                new FieldInfo(\\"age\\", new IntFormatInfo()))\\n                );\\n        List<FilterFunction> filters = Arrays.asList(\\n                new SingleValueFilterFunction(EmptyOperator.getInstance(),\\n                        new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                        LessThanOperator.getInstance(), new ConstantParam(25)),\\n                new SingleValueFilterFunction(AndOperator.getInstance(),\\n                        new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                        MoreThanOrEqualOperator.getInstance(), new ConstantParam(18))\\n        );\\n        return new KafkaLoadNode(\\"2\\", \\"kafka_output\\", fields, relations, filters,\\n                filterStrategy, \\"topic1\\", \\"localhost:9092\\",\\n                new CanalJsonFormat(), null,\\n                null, \\"id\\");\\n    }\\n```\\n\\n\u751f\u6210\u7684 SQL \u4e3a\uff1a\\n\\n```sql\\nCREATE TABLE `kafka_3` (`name` string,`age` int) \\nwith (\\n\'connector\' = \'kafka-inlong\',\\n\'topic\' = \'topic1\',\\n\'properties.bootstrap.servers\' = \'localhost:9092\',\\n\'format\' = \'canal-json-inlong\',\\n\'canal-json-inlong.ignore-parse-errors\' = \'true\',\\n\'canal-json-inlong.map-null-key.mode\' = \'DROP\',\\n\'canal-json-inlong.encode.decimal-as-plain-number\' = \'true\',\\n\'canal-json-inlong.timestamp-format.standard\' = \'SQL\',\\n\'canal-json-inlong.map-null-key.literal\' = \'null\'\\n)\\n```\\n\\n### 4.2 \u5b57\u6bb5 T \u751f\u6210 SQL\\n\\n#### 4.2.1 \u8fc7\u6ee4\u7b97\u5b50\\n\\n\u76f8\u5173\u914d\u7f6e\u89c1 4.1 \u8282\u70b9\u914d\u7f6e\\n\\n\u751f\u6210\u7684 SQL \u4e3a\uff1a\\n\\n```sql\\nINSERT INTO `kafka_3` SELECT `name` AS `name`,`age` AS `age` FROM `mysql_1` WHERE `age` < 25 AND `age` >= 18\\n```\\n\\n#### 4.2.2 \u6c34\u4f4d\u7ebf\\n\\nGroupInfo \u5b8c\u6574\u914d\u7f6e\u5982\u4e0b\uff1a\\n\\n```java\\nprivate Node buildMySqlExtractNode() {\\n        List<FieldInfo> fields = Arrays.asList(\\n                new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                new FieldInfo(\\"ts\\", new TimestampFormatInfo()));\\n        WatermarkField wk = new WatermarkField(new FieldInfo(\\"ts\\", new TimestampFormatInfo()),\\n                new StringConstantParam(\\"1\\"),\\n                new TimeUnitConstantParam(TimeUnit.MINUTE));\\n        return new MySqlExtractNode(\\"1\\", \\"mysql_input\\", fields,\\n                wk, null, \\"id\\",\\n                Collections.singletonList(\\"tableName\\"), \\"localhost\\", \\"root\\", \\"password\\",\\n                \\"inlong\\", null, null,\\n                null, null);\\n    }\\n\\n    private Node buildKafkaNode() {\\n        List<FieldInfo> fields = Arrays.asList(\\n                new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                new FieldInfo(\\"ts\\", new TimestampFormatInfo()));\\n        List<FieldRelation> relations = Arrays\\n                .asList(new FieldRelation(new FieldInfo(\\"name\\", new StringFormatInfo()),\\n                                new FieldInfo(\\"name\\", new StringFormatInfo())),\\n                        new FieldRelation(new FieldInfo(\\"age\\", new IntFormatInfo()),\\n                                new FieldInfo(\\"age\\", new IntFormatInfo()))\\n                );\\n        return new KafkaLoadNode(\\"2\\", \\"kafka_output\\", fields, relations, null, null,\\n                \\"topic\\", \\"localhost:9092\\", new JsonFormat(),\\n                1, null, \\"id\\");\\n    }\\n\\n    private NodeRelation buildNodeRelation(List<Node> inputs, List<Node> outputs) {\\n        List<String> inputIds = inputs.stream().map(Node::getId).collect(Collectors.toList());\\n        List<String> outputIds = outputs.stream().map(Node::getId).collect(Collectors.toList());\\n        return new NodeRelation(inputIds, outputIds);\\n    }\\n\\n    @Override\\n    public GroupInfo getTestObject() {\\n        Node input = buildMySqlExtractNode();\\n        Node output = buildKafkaNode();\\n        StreamInfo streamInfo = new StreamInfo(\\"1\\", Arrays.asList(input, output), Collections.singletonList(\\n                buildNodeRelation(Collections.singletonList(input), Collections.singletonList(output))));\\n        return new GroupInfo(\\"1\\", Collections.singletonList(streamInfo));\\n    }\\n```"},{"id":"/2022/04/25/release-1.1.0","metadata":{"permalink":"/zh-CN/blog/2022/04/25/release-1.1.0","editUrl":"https://github.com/apache/inlong-website/edit/master/blog/blog/2022-04-25-release-1.1.0.md","source":"@site/i18n/zh-CN/docusaurus-plugin-content-blog/2022-04-25-release-1.1.0.md","title":"1.1.0 \u7248\u672c\u53d1\u5e03","description":"Apache InLong\uff08\u5e94\u9f99\uff09\u662f\u4e00\u4e2a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002InLong\u652f\u6301\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002","date":"2022-04-25T00:00:00.000Z","formattedDate":"2022\u5e744\u670825\u65e5","tags":[],"readingTime":6.235,"truncated":false,"authors":[],"frontMatter":{"title":"1.1.0 \u7248\u672c\u53d1\u5e03"},"prevItem":{"title":"InLong Sort ETL \u65b9\u6848\u89e3\u6790","permalink":"/zh-CN/blog/2022/06/16/inlong-sort-etl"},"nextItem":{"title":"0.12.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2022/01/04/release-0.12.0"}},"content":"Apache InLong\uff08\u5e94\u9f99\uff09\u662f\u4e00\u4e2a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002InLong\u652f\u6301\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002\\n\\n## 1.1.0 \u7248\u672c\u7279\u6027\u603b\u89c8\\n\u521a\u521a\u53d1\u5e03\u7684 1.1.0-incubating \u4e3b\u8981\u5305\u62ec\u4ee5\u4e0b\u5185\u5bb9\uff1a\\n\\n### \u7ba1\u63a7\u80fd\u529b\u589e\u5f3a\\n- \u589e\u52a0 Manager Client\uff0c\u652f\u6301\u96c6\u6210 InLong \u521b\u5efa\u4efb\u52a1\\n- \u589e\u52a0 ManagerCtl \u547d\u4ee4\u884c\u5de5\u5177\uff0c\u652f\u6301\u67e5\u770b\u3001\u521b\u5efa\u6570\u636e\u6d41\\n- Manager \u652f\u6301\u4e0b\u53d1 Agent \u4efb\u52a1\\n- Manager \u652f\u6301\u4e0b\u53d1 Sort Flink \u4efb\u52a1\\n- Manger \u6d41\u5411\u4efb\u52a1\u7ba1\u7406\uff0c\u652f\u6301\u542f\u52a8\u3001\u91cd\u542f\u3001\u6682\u505c\u64cd\u4f5c\\n- Manager \u63d2\u4ef6\u5316\u6539\u9020\\n- Manager \u5143\u6570\u636e\u7ba1\u7406\u652f\u6301\u4f7f\u7528 MySQL\\n- \u96c6\u7fa4\u7ba1\u7406\u4e00\u671f\uff0c\u7edf\u4e00\u96c6\u7fa4\u4fe1\u606f\u6ce8\u518c\\n\\n### \u6570\u636e\u8282\u70b9\u4e30\u5bcc\\n- \u652f\u6301 Iceberg \u6d41\u5411\u5165\u5e93\\n- \u652f\u6301 ClickHouse \u6d41\u5411\u5165\u5e93\\n- \u652f\u6301 MySQL Binlog \u91c7\u96c6\\n- \u652f\u6301 Kafka \u91c7\u96c6\\n- Sort Standalone \u652f\u6301\u591a\u6d41\u5411\\n\\n### \u914d\u5957\u5de5\u5177\u5efa\u8bbe\\n- Dashboard \u63d2\u4ef6\u5316\u6539\u9020\\n- Kubernetes \u90e8\u7f72\u4f18\u5316\\n- Standalone \u90e8\u7f72\u91cd\u6784\\n\\n### \u7cfb\u7edf\u5347\u7ea7\\n- Protocol Buffers \u5347\u7ea7\\n- \u7edf\u4e00\u7248\u672c Maven \u7248\u672c\u4f9d\u8d56\\n- \u4fee\u590d\u4e00\u6279\u4f9d\u8d56 CVEs \u95ee\u9898\\n- DataProxy \u652f\u6301 PB \u538b\u7f29\u534f\u8bae\\n\\n\u8be5\u7248\u672c\u5173\u95ed\u4e86\u7ea6 642+ \u4e2a issue\uff0c\u5305\u542b 23+ \u4e2a\u91cd\u5927 feature \u548c 180+ \u4e2a improvements\u3002\\n\\n## 1.1.0 \u7248\u672c\u7279\u6027\u4ecb\u7ecd\\n### \u589e\u52a0 Manager Client\\n\u65b0\u589e\u7684 Manager Client\uff0c\u5b9a\u4e49\u4e86\u5e38\u89c1\u7684 InLong Group/Stream \u64cd\u4f5c\u63a5\u53e3\uff0c\u5305\u62ec\u4efb\u52a1\u7684\u521b\u5efa\u3001\u67e5\u770b\u548c\u5220\u9664\u3002\u7528\u6237\u901a\u8fc7 Manager Client\uff0c\u53ef\u4ee5\u5c06 InLong \u5185\u7f6e\u5230\u4efb\u4f55\u7b2c\u4e09\u65b9\u5e73\u53f0\u4e2d\uff0c\u5b9e\u73b0\u7edf\u4e00\u7684\u5b9a\u5236\u5316\u5e73\u53f0\u5efa\u8bbe\u3002Manager Client \u4e3b\u8981\u662f\u7531 @kipshi \u3001 @gong\u3001@ciscozhou \u8bbe\u8ba1\u548c\u5b9e\u73b0\uff0c\u611f\u8c22\u4e09\u4f4d\u8d21\u732e\u8005\u3002\\n\\n### \u589e\u52a0 ManagerCtl \u547d\u4ee4\u884c\u5de5\u5177\\nManagerCtl \u57fa\u4e8e Manager Client \u5f00\u53d1\u5b8c\u6210\uff0c\u662f\u4e00\u4e2a\u64cd\u4f5c InLong \u8d44\u6e90\u7684\u547d\u4ee4\u884c\u5de5\u5177\uff0c\u53ef\u4ee5\u8fdb\u4e00\u6b65\u7b80\u5316\u5f00\u53d1\u8005\u7684\u4f7f\u7528\u3002ManagerCtl \u662f\u7531 @haifxu \u72ec\u7acb\u8d21\u732e\uff0c\u5305\u62ec\u6307\u5f15\u5305\u62ec\uff1a\\n```\\nUsage: managerctl [options] [command] [command options]\\nOptions:\\n-h, --help\\nGet all command about managerctl.\\nCommands:\\ncreate      Create resource by json file\\nUsage: create [options]\\n\u200b\\ndescribe      Display details of one or more resources\\nUsage: describe [options]\\n\u200b\\nlist      Displays main information for one or more resources\\nUsage: list [options]\\n```\\n\\n### Manager \u652f\u6301\u4e0b\u53d1 Sort \u4efb\u52a1\\n\u5728\u4e4b\u524d\u7248\u672c\uff0c\u7528\u6237\u521b\u5efa\u5b8c\u6570\u636e\u6d41 Group/Stream \u4efb\u52a1\u540e\uff0cSort \u9700\u8981\u901a\u8fc7\u547d\u4ee4\u884c\u63d0\u4ea4\u5230 Flink \u96c6\u7fa4\uff0c\u518d\u53bb\u6267\u884c\u6570\u636e\u5206\u62e3\u5de5\u4f5c\u3002\u5728 1.1.0 \u7248\u672c\u4e2d\uff0c\u6211\u4eec\u5c06 Sort \u4efb\u52a1\u7684\u542f\u52a8\u3001\u505c\u6b62\u3001\u6302\u8d77\u64cd\u4f5c\uff0c\u7edf\u4e00\u5230 Manager \u5b8c\u6210\uff0c\u7528\u6237\u53ea\u9700\u8981\u5728 Manager \u90e8\u7f72\u65f6\u914d\u7f6e\u6b63\u786e\u7684 Flink \u96c6\u7fa4\uff0c\u5f53\u6570\u636e\u6d41\u5ba1\u6279\u901a\u8fc7\u540e\uff0c\u4f1a\u81ea\u52a8\u62c9\u8d77 Sort \u4efb\u52a1\u3002\u8be5\u90e8\u5206\u5de5\u4f5c\u4e3b\u8981\u662f\u7531 @LvJiancheng \u8d21\u732e\u5b8c\u6210\u3002\\n\\n### Manager \u5143\u6570\u636e\u4fdd\u5b58\u53bb ZooKeeper\\nInLong \u4f7f\u7528 ZooKeeper \u4fdd\u5b58\u6570\u636e\u6d41\u5143\u6570\u636e\uff0c\u589e\u52a0\u4e86\u5f00\u53d1\u8005\u548c\u7528\u6237\u7684\u90e8\u7f72\u95e8\u69db\u548c\u8fd0\u7ef4\u96be\u5ea6\u3002\u5728 1.1.0 \u7248\u672c\u4e2d\uff0c\u6211\u4eec\u9ed8\u8ba4\u5c06\u6570\u636e\u6d41\u5143\u6570\u636e\u4fdd\u5b58\u5728 DB \u4e2d\uff0cZooKeeper \u53ea\u4f5c\u4e3a\u5927\u89c4\u6a21\u573a\u666f\u4e0b\u53ef\u9009\u65b9\u6848\u3002\u611f\u8c22 @kipshi @yunqingmoswu \u8d21\u732e\u4e86\u53bb ZooKeeper \u7684 PR\u3002\\n\\n### \u652f\u6301 MySQL Binlog \u91c7\u96c6\\n1.1.0 \u7248\u672c\u5b8c\u6574\u652f\u6301\u4e86\u4ece MySQL \u91c7\u96c6\u6570\u636e\uff0c\u652f\u6301\u589e\u91cf\u548c\u5168\u91cf\u4e24\u79cd\u7b56\u7565\uff0c\u7528\u6237\u53ef\u4ee5\u5728 InLong \u7b80\u5355\u914d\u7f6e\u5c31\u53ef\u4ee5\u5b9e\u73b0 MySQL \u6570\u636e\u7684\u91c7\u96c6\u3002\u8be5\u90e8\u5206\u5de5\u4f5c\u662f\u7531 @EMsnap \u8d21\u732e\u5b8c\u6210\u3002\\n\\n### Sort \u65b0\u589e Iceberg\u3001 ClickHouse\u3001 Kafka \u6d41\u5411\u5165\u5e93\\n1.1.0 \u7248\u672c\u4e2d\u589e\u52a0\u4e86\u591a\u79cd\u573a\u666f\u6570\u636e\u8282\u70b9\u7684\u5165\u5e93\uff0c\u5305\u62ec Iceberg\u3001 ClickHouse\u3001 Kafka\uff0c\u8fd9\u4e09\u79cd\u6d41\u5411\u7684\u652f\u6301\u4e30\u5bcc\u4e86 InLong \u7684\u4f7f\u7528\u573a\u666f\u3002\u65b0\u6d41\u5411\u7684\u652f\u6301\uff0c\u4e3b\u8981\u7531@chantccc @KevinWen007 @healchow \u53c2\u4e0e\u8d21\u732e\u3002\\n\\n### Sort Standalone \u652f\u6301 Hive\u3001Elasticsearch\u3001Kafka\\n\u4e4b\u524d\u7248\u672c\u6709\u63d0\u5230\uff0c\u5bf9\u4e8e\u975e Flink \u73af\u5883\uff0c\u6211\u4eec\u53ef\u4ee5\u901a\u8fc7 Sort Standalone \u6765\u8fdb\u884c\u6570\u636e\u6d41\u5206\u62e3\u3002\u5728 1.1.0 \u7248\u672c\u4e2d\uff0c\u6211\u4eec\u589e\u52a0\u4e86\u5bf9 Hive\u3001ElasticSearch\u3001Kafka \u7684\u652f\u6301\uff0c\u6269\u5c55\u4e86 Sort Standalone \u7684\u4f7f\u7528\u573a\u666f\u3002Sort Standalone \u4e3b\u8981\u6709 @vernedeng @luchunliang \u53c2\u4e0e\u8d21\u732e\u3002\\n\\n### Protocol Buffers \u5347\u7ea7\\nInLong \u6240\u6709\u7ec4\u4ef6 Protocol Buffers \u4f9d\u8d56\u4ece 2.5.0 \u5347\u7ea7\u5230 3.19.4\uff0c\u611f\u8c22 @gosonzhang @doleyzi \u7684\u8d21\u732e\uff0c\u4e3a Protocol Buffers \u5347\u7ea7\u505a\u7684\u5927\u91cf\u517c\u5bb9\u548c\u6d4b\u8bd5\u5de5\u4f5c\u3002\\n\\n### InLong on Kubernetes \u4f18\u5316\\nInLong on Kubernetes \u7684\u4f18\u5316\u5de5\u4f5c\u4e3b\u8981\u5305\u62ec\u589e\u52a0 Audit\u3001\u68b3\u7406\u914d\u7f6e\u3001\u4f18\u5316\u6d88\u606f\u961f\u5217\u4f7f\u7528\u3001\u4f18\u5316\u6587\u6863\u4f7f\u7528\u7b49\uff0c\u65b9\u4fbf InLong \u4e0a\u4e91\u7684\u4f7f\u7528\u3002\u611f\u8c22 @shink \u4e3a\u8fd9\u4e9b\u4f18\u5316\u505a\u7684\u8d21\u732e\u3002\\n\\n### Dashboard \u63d2\u4ef6\u5316\\n\u4e3a\u4e86\u65b9\u4fbf\u7528\u6237\u5feb\u901f\u5728 Dashboard \u6784\u5efa\u65b0\u7684\u6d41\u5411\uff0c1.1.0 \u7248\u672c\u4e2d\u5b9e\u73b0\u4e86 Dashboard \u63d2\u4ef6\u5316\uff0c\u4e86\u89e3 JavaScript \u5f00\u53d1\u8005\u57fa\u4e8e\u63d2\u4ef6\u5f00\u53d1\u6307\u5f15\uff0c\u53ef\u4ee5\u5feb\u901f\u6269\u5c55\u65b0\u7684\u6570\u636e\u6d41\u5411\u3002\u8fd9\u90e8\u5206\u5de5\u4f5c\u611f\u8c22 @leezng\\n\\n### \u5176\u4ed6\u7279\u6027\u53ca\u95ee\u9898\u4fee\u590d\\n\u76f8\u5173\u5185\u5bb9\u8bf7\u53c2\u8003[\u7248\u672c\u8bf4\u660e](https://github.com/apache/incubator-inlong/blob/master/CHANGES.md)\uff0c\u5176\u4e2d\u8be6\u7ec6\u5217\u51fa\u4e86\u672c\u6b21\u7248\u672c\u7684\u7279\u6027\u3001\u63d0\u5347 \u548c Bug \u4fee\u590d\uff0c\u4ee5\u53ca\u5177\u4f53\u7684\u8d21\u732e\u8005\u3002\\n\\n## Apache InLong(incubating) \u540e\u7eed\u89c4\u5212\\n\u540e\u7eed\u7248\u672c\uff0c\u6211\u4eec\u5c06\u652f\u6301\u8f7b\u91cf\u5316\u7684 Sort\uff0c\u4ee5\u53ca\u6269\u5c55\u66f4\u591a\u7684\u6570\u636e\u6e90\u7aef\u548c\u76ee\u6807\u7aef\uff0c\u8986\u76d6\u66f4\u591a\u7684\u4f7f\u7528\u573a\u666f\uff0c\u4e3b\u8981\u5305\u62ec\uff1a\\n- Flink SQL \u652f\u6301\\n- Elasticsearch\u3001HBase \u6d41\u5411\u652f\u6301"},{"id":"/2022/01/04/release-0.12.0","metadata":{"permalink":"/zh-CN/blog/2022/01/04/release-0.12.0","editUrl":"https://github.com/apache/inlong-website/edit/master/blog/blog/2022-01-04-release-0.12.0.md","source":"@site/i18n/zh-CN/docusaurus-plugin-content-blog/2022-01-04-release-0.12.0.md","title":"0.12.0 \u7248\u672c\u53d1\u5e03","description":"InLong(\u5e94\u9f99) : \u4e2d\u56fd\u795e\u8bdd\u6545\u4e8b\u91cc\u7684\u795e\u517d\uff0c\u5f15\u6d41\u5165\u6d77\uff0c\u501f\u55bb InLong \u7cfb\u7edf\u63d0\u4f9b\u6570\u636e\u63a5\u5165\u80fd\u529b\u3002","date":"2022-01-04T00:00:00.000Z","formattedDate":"2022\u5e741\u67084\u65e5","tags":[],"readingTime":8.66,"truncated":false,"authors":[],"frontMatter":{"title":"0.12.0 \u7248\u672c\u53d1\u5e03"},"prevItem":{"title":"1.1.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2022/04/25/release-1.1.0"},"nextItem":{"title":"0.11.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2021/11/10/release-0.11.0"}},"content":"InLong(\u5e94\u9f99) : \u4e2d\u56fd\u795e\u8bdd\u6545\u4e8b\u91cc\u7684\u795e\u517d\uff0c\u5f15\u6d41\u5165\u6d77\uff0c\u501f\u55bb InLong \u7cfb\u7edf\u63d0\u4f9b\u6570\u636e\u63a5\u5165\u80fd\u529b\u3002\\n\\nApache InLong\uff08\u5e94\u9f99\uff09\u662f\u4e00\u4e2a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002InLong\u652f\u6301\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002\\n\\n\u521a\u521a\u53d1\u5e03\u7684 0.12.0-incubating \u4e3b\u8981\u5305\u62ec\u4ee5\u4e0b\u5185\u5bb9\uff1a\\n- \u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u540c\u65f6\u652f\u6301\u6279\u548c\u6d41\\n- \u5bf9\u5b98\u7f51\u6587\u6863\u7ed3\u6784\u8fdb\u884c\u91cd\u6784\uff0c\u65b9\u4fbf\u7528\u6237\u6839\u636e\u4e3b\u7ebf\u67e5\u9605\u76f8\u5173\u6587\u6863\\n- \u5168\u6d41\u7a0b\u652f\u6301Pulsar\u6570\u636e\u6d41\u5411\uff0c\u5b8c\u6210\u9ad8\u6027\u80fd\u3001\u9ad8\u53ef\u9760\u573a\u666f\u7684\u5168\u6d41\u7a0b\u8986\u76d6\\n- \u5168\u6d41\u7a0b\u652f\u6301\u6307\u6807\uff0c\u5305\u62ec JMX \u548c Prometheus \u8f93\u51fa\\n- \u6570\u636e\u5ba1\u8ba1\u5bf9\u8d26\u4e00\u671f\uff0c\u5c06\u5ba1\u8ba1\u6570\u636e\u5199\u5165 MySQL\\n\\n\u8be5\u7248\u672c\u5173\u95ed\u4e86\u7ea6 120+ \u4e2a issue\uff0c\u5305\u542b\u56db\u4e2a\u91cd\u5927 feature \u548c 35 \u4e2a improvements\u3002\\n\\n### Apache InLong(incubating) \u7b80\u4ecb\\n[Apache InLong\uff08\u5e94\u9f99\uff09](https://inlong.apache.org/zh-cn/) \u662f\u817e\u8baf\u6350\u732e\u7ed9 Apache \u793e\u533a\u7684\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002 InLong \u9879\u76ee\u539f\u540d TubeMQ \uff0c\u4e13\u6ce8\u4e8e\u9ad8\u6027\u80fd\u3001\u4f4e\u6210\u672c\u7684\u6d88\u606f\u961f\u5217\u670d\u52a1\u3002\u4e3a\u4e86\u8fdb\u4e00\u6b65\u91ca\u653e TubeMQ \u5468\u8fb9\u7684\u751f\u6001\u80fd\u529b\uff0c\u6211\u4eec\u5c06\u9879\u76ee\u5347\u7ea7\u4e3a InLong\uff0c\u4e13\u6ce8\u6253\u9020\u4e00\u7ad9\u5f0f\u6570\u636e\u6d41\u63a5\u5165\u670d\u52a1\u5e73\u53f0\u3002\\n\\nApache InLong \u4ee5\u817e\u8baf\u5185\u90e8\u4f7f\u7528\u7684 TDBank \u4e3a\u539f\u578b\uff0c\u5177\u6709\u4e07\u4ebf\u7ea7\u6570\u636e\u7684\u63a5\u5165\u548c\u5904\u7406\u80fd\u529b\uff0c\u6574\u5408\u4e86\u6570\u636e\u91c7\u96c6\u3001\u6c47\u805a\u3001\u5b58\u50a8\u3001\u5206\u62e3\u6570\u636e\u5904\u7406\u5168\u6d41\u7a0b\uff0c\u62e5\u6709\u7b80\u5355\u6613\u7528\u3001\u7075\u6d3b\u6269\u5c55\u3001\u7a33\u5b9a\u53ef\u9760\u7b49\u7279\u6027\u3002\\n<img src=\\"../img/inlong-structure-zh.png\\" align=\\"center\\" alt=\\"Apache InLong\\"/>\\n\\n Apache InLong \u670d\u52a1\u4e8e\u6570\u636e\u91c7\u96c6\u5230\u843d\u5730\u7684\u6574\u4e2a\u751f\u547d\u5468\u671f\uff0c\u6309\u6570\u636e\u7684\u4e0d\u540c\u9636\u6bb5\u63d0\u4f9b\u4e0d\u540c\u7684\u5904\u7406\u6a21\u5757\uff0c\u4e3b\u8981\u5305\u62ec\uff1a\\n - **inlong-agent** \uff0c\u6570\u636e\u91c7\u96c6 Agent \uff0c\u652f\u6301\u4ece\u6307\u5b9a\u76ee\u5f55\u6216\u6587\u4ef6\u8bfb\u53d6\u5e38\u89c4\u65e5\u5fd7\uff0c\u8fdb\u884c\u9010\u6761\u7684\u6570\u636e\u4e0a\u62a5\u3002\u540e\u7eed\u4e5f\u5c06\u6269\u5c55 DB \u91c7\u96c6\uff0c\u6269\u5c55HTTP\u4e0a\u62a5\u7b49\u80fd\u529b\u3002\\n - **inlong-dataproxy** \uff0c\u4e00\u4e2a\u57fa\u4e8e Flume-ng \u7684 Proxy \u7ec4\u4ef6\uff0c\u652f\u6301\u6570\u636e\u53d1\u9001\u963b\u585e\u3001\u843d\u76d8\u91cd\u53d1\uff0c\u62e5\u6709\u5c06\u63a5\u6536\u6570\u636e\u540e\u8f6c\u53d1\u5230\u4e0d\u540cMQ\uff08\u6d88\u606f\u961f\u5217\uff09\u7684\u80fd\u529b\u3002\\n - **inlong-tubemq** \uff0c\u817e\u8baf\u81ea\u7814\u7684\u6d88\u606f\u961f\u5217\u670d\u52a1\uff0c\u4e13\u6ce8\u670d\u52a1\u5927\u6570\u636e\u573a\u666f\u4e0b\u6d77\u91cf\u6570\u636e\u7684\u9ad8\u6027\u80fd\u5b58\u50a8\u548c\u4f20\u8f93\uff0c\u5728\u6d77\u91cf\u5b9e\u8df5\u548c\u4f4e\u6210\u672c\u65b9\u9762\u6709\u7740\u6bd4\u8f83\u597d\u7684\u6838\u5fc3\u4f18\u52bf\u3002\\n - **inlong-sort** \uff0c\u4ece\u4e0d\u540c\u7684 MQ \u6d88\u8d39\u6570\u636e\u540e\u8fdb\u884c ETL \u5904\u7406\uff0c\u7136\u540e\u5c06\u6570\u636e\u6c47\u805a\u5e76\u5199\u5165 Hive\u3001ClickHouse\u3001Hbase\u3001IceBerg \u7b49\u3002\\n - **inlong-manager** \uff0c\u63d0\u4f9b\u5b8c\u6574\u7684\u6570\u636e\u670d\u52a1\u7ba1\u63a7\u80fd\u529b\uff0c\u5305\u62ec\u5143\u6570\u636e\u3001OpenAPI\u3001\u4efb\u52a1\u6d41\u3001\u6743\u9650\u7b49\u3002\\n - **inlong-website** \uff0c\u4e00\u4e2a\u7528\u4e8e\u7ba1\u7406\u6570\u636e\u63a5\u5165\u7684\u524d\u7aef\u9875\u9762\uff0c\u7b80\u5316\u6574\u4e2a InLong \u7ba1\u63a7\u5e73\u53f0\u7684\u4f7f\u7528\u3002\\n\\n### Apache InLong(incubating) 0.12.0 \u4e3b\u8981\u7279\u6027\\n#### 1. \u6253\u901a Apache Pulsar \u5168\u94fe\u8def\\n\u5728 0.12.0 \u7248\u672c\u4e2d\uff0c\u6211\u4eec\u8865\u9f50\u4e86 FileAgent \u2192 DataProxy \u2192 Pulsar \u2192 Sort \u7684\u6570\u636e\u4e0a\u62a5\u80fd\u529b\uff0c\u81f3\u6b64\uff0cInLong \u652f\u6301\u9ad8\u6027\u80fd\u548c\u9ad8\u53ef\u9760\u6570\u636e\u63a5\u5165\u573a\u666f\uff1a\u76f8\u6bd4\u9ad8\u541e\u5410\u7684 TubeMQ\uff0cApache Pulsar\u80fd\u63d0\u4f9b\u66f4\u597d\u7684\u6570\u636e\u4e00\u81f4\u6027\uff0c\u66f4\u9002\u7528\u4e8e\u91d1\u878d\u3001\u8ba1\u8d39\u7b49\u5bf9\u6570\u636e\u53ef\u9760\u6027\u8981\u6c42\u6781\u9ad8\u7684\u573a\u666f\u3002\\n<img src=\\"/img/pulsar-arch-zh.png\\" align=\\"center\\" alt=\\"Report via Pulsar\\"/>\\n\\n\u611f\u8c22 @healchow\u3001 @baomingyu\u3001@leezng\u3001@bruceneenhl\u3001@ifndef-SleePy \u7b49\u540c\u5b66\u5bf9\u8fd9\u4e2a\u7279\u6027\u7684\u8d21\u732e\uff0c\u66f4\u591a\u4fe1\u606f\u8bf7\u53c2\u8003\uff0c\u76f8\u5173 PR \u89c1 [INLONG-1310](https://github.com/apache/incubator-inlong/issues/1310) \uff0c\u4f7f\u7528\u6307\u5f15\u89c1 [Pulsar\u4f7f\u7528\u793a\u4f8b](https://inlong.apache.org/zh-CN/docs/next/quick_start/pulsar_example/) \u3002\\n\\n#### 2. \u652f\u6301 JMX \u548c Prometheus \u6307\u6807\\n\u5728\u5df2\u6709\u7684\u4ee5\u6587\u4ef6\u8f93\u51fa\u6307\u6807\u4e4b\u5916\uff0cInLong \u7684\u5404\u4e2a\u7ec4\u4ef6\u5f00\u59cb\u9010\u6b65\u652f\u6301 JMX \u548c Prometheus \u6307\u6807\u7684\u8f93\u51fa\uff0c\u4ee5\u63d0\u5347\u6574\u4e2a\u7cfb\u7edf\u7684\u53ef\u89c1\u6027\u3002\u76ee\u524d\u5305\u62ec Agent\uff0cDataProxy\uff0cTubeMQ\uff0cSort-Standalone \u7b49\u6a21\u5757\u5df2\u7ecf\u652f\u6301\u4e0a\u8ff0\u6307\u6807\uff0c\u5404\u4e2a\u6a21\u5757\u8f93\u51fa\u7684\u6307\u6807\u7684\u6587\u6863\u6b63\u5728\u6574\u7406\u4e2d\u3002\\n\\n\u611f\u8c22 @shink\uff0c@luchunliang\uff0c@EMsnap\uff0c@gosonzhang \u7b49\u540c\u5b66\u7684\u8d21\u732e\uff0c\u76f8\u5173 PR \u89c1[INLONG-1712](https://github.com/apache/incubator-inlong/issues/1712) \uff0c[INLONG-1786](https://github.com/apache/incubator-inlong/issues/1786) \uff0c[INLONG-1796](https://github.com/apache/incubator-inlong/issues/1796) \uff0c[INLONG-1827](https://github.com/apache/incubator-inlong/issues/1827) \uff0c[INLONG-1851](https://github.com/apache/incubator-inlong/issues/1851) \uff0c[INLONG-1926](https://github.com/apache/incubator-inlong/issues/1926) \u3002\\n\\n#### 3. \u6a21\u5757\u80fd\u529b\u6269\u5145\\nSort \u6a21\u5757\u589e\u52a0\u4e86\u5bf9 Apache Doris \u5b58\u50a8\u7684\u652f\u6301\uff0c\u5b9e\u73b0\u4e86 Sort \u5206\u62e3\u6570\u636e\u843d\u5730\u5230 Apache Doris \u7684\u80fd\u529b\uff0c\u4f7f InLong \u7684\u5165\u5e93\u9009\u62e9\u53c8\u591a\u4e86\u4e00\u9879\u3002\u6b64\u5916\uff0c\u4e3a\u4e86\u4e30\u5bcc\u6570\u636e\u63a5\u5165\u5168\u6d41\u7a0b\u7684\u529f\u80fd\uff0c\u589e\u52a0\u4e86 Audit\u3001Sort-Standalone \u6a21\u5757\uff1a\\n- Audit \u6a21\u5757\u63d0\u4f9b\u6570\u636e\u6d41\u5168\u6d41\u7a0b\u7684\u5bf9\u8d26\u80fd\u529b\uff0c\u901a\u8fc7\u6570\u636e\u6d41\u6307\u6807\u6765\u76d1\u63a7\u7cfb\u7edf\u7684\u670d\u52a1\u8d28\u91cf\uff1b\\n- Sort-Standalone \u6a21\u5757\u662f\u4e00\u4e2a\u4e0d\u57fa\u4e8e Flink \u7684\u6570\u636e\u5206\u62e3\u6a21\u5757\uff0c\u7ed9\u7cfb\u7edf\u589e\u52a0\u4e86\u4e00\u4e2a\u8f7b\u91cf\u7ea7\u7684\u6570\u636e\u5206\u62e3\u80fd\u529b\uff0c\u4fbf\u4e8e\u4e1a\u52a1\u5feb\u901f\u642d\u5efa\u53ca\u4f7f\u7528\u3002\\n\\nAudit\u3001Sort-Standalone \u6a21\u5757\u4ecd\u5728\u5f00\u53d1\u4e2d\uff0c\u7248\u672c\u7a33\u5b9a\u540e\u5c06\u5bf9\u5916\u53d1\u5e03\u4f7f\u7528\u3002\\n\\n\u611f\u8c22 @huzk8\uff0c@doleyzi\uff0c@luchunliang \u7b49\u540c\u5b66\u7684\u8d21\u732e\uff0c\u76f8\u5173 PR \u89c1 [INLONG-1821](https://github.com/apache/incubator-inlong/issues/1821) \uff0c[INLONG-1738](https://github.com/apache/incubator-inlong/issues/1738) \uff0c[INLONG-1889](https://github.com/apache/incubator-inlong/issues/1889) \u3002\\n\\n#### 4. \u5b98\u7f51\u6587\u6863\u76ee\u5f55\u91cd\u6784\\n0.12.0 \u7248\u672c\u5bf9\u529f\u80fd\u6a21\u5757\u7684\u6539\u8fdb\u4e4b\u5916\uff0c\u5b98\u7f51\u7ed3\u6784\u4ee5\u53ca\u4f7f\u7528\u6587\u6863\u65b9\u9762\u4e5f\u505a\u4e86\u6df1\u5ea6\u8c03\u6574\uff0c\u5305\u62ec\u91cd\u6784\u6587\u6863\u7684\u76ee\u5f55\u7ed3\u6784\uff0c\u8865\u5145\u5b8c\u5584\u5404\u4e2a\u6a21\u5757\u7684\u529f\u80fd\u4ecb\u7ecd\uff0c\u589e\u52a0\u6587\u6863\u7ffb\u8bd1\uff0c\u8fdb\u4e00\u6b65\u63d0\u9ad8 InLong \u5b98\u7f51\u7684\u6587\u6863\u53ef\u9605\u8bfb\u6027\uff0c\u5b9e\u73b0\u5feb\u901f\u67e5\u627e\u3001\u65b9\u4fbf\u9605\u8bfb\u3002\u8fd9\u5757\u5185\u5bb9\u53ef\u4ee5\u67e5\u770b\u5b98\u7f51\u8fdb\u884c\u4e86\u89e3\uff0c\u76ee\u524d\u6587\u6863\u8fd8\u5728\u6301\u7eed\u5efa\u8bbe\u4e2d\uff0c\u6b22\u8fce\u5927\u5bb6\u63d0\u51fa\u5b9d\u8d35\u7684\u610f\u89c1\u6216\u5efa\u8bae\u3002\\n\\n\u611f\u8c22 @bluewang\uff0c@dockerzhang\uff0c@healchow \u7b49\u540c\u5b66\u7684\u8d21\u732e\uff0c\u76f8\u5173 PR \u89c1 [INLONG-1711](https://github.com/apache/incubator-inlong/issues/1711) \uff0c[INLONG-1740](https://github.com/apache/incubator-inlong/issues/1740) \uff0c[INLONG-1802](https://github.com/apache/incubator-inlong/issues/1802) \uff0c[INLONG-1809](https://github.com/apache/incubator-inlong/issues/1809) \uff0c[INLONG-1810](https://github.com/apache/incubator-inlong/issues/1810) \uff0c[INLONG-1815](https://github.com/apache/incubator-inlong/issues/1815) \uff0c[INLONG-1822](https://github.com/apache/incubator-inlong/issues/1822) \uff0c[INLONG-1840](https://github.com/apache/incubator-inlong/issues/1840) \uff0c[INLONG-1856](https://github.com/apache/incubator-inlong/issues/1856) \uff0c[INLONG-1861](https://github.com/apache/incubator-inlong/issues/1861) \uff0c[INLONG-1867](https://github.com/apache/incubator-inlong/issues/1867) \uff0c[INLONG-1878](https://github.com/apache/incubator-inlong/issues/1878) \uff0c[INLONG-1901](https://github.com/apache/incubator-inlong/issues/1901) \uff0c[INLONG-1939](https://github.com/apache/incubator-inlong/issues/1939) \u3002\\n\\n#### 5. \u5176\u4ed6\u7279\u6027\u53ca\u95ee\u9898\u4fee\u590d\\n\u76f8\u5173\u5185\u5bb9\u8bf7\u53c2\u8003[\u7248\u672c\u53d1\u7248\u8bf4\u660e](https://github.com/apache/incubator-inlong/blob/0.12.0-incubating-RC0/CHANGES.md) \uff0c\u91cc\u9762\u5217\u51fa\u4e86\u672c\u6b21\u7248\u672c\u8be6\u7ec6\u7684\u7279\u6027\u3001\u6539\u8fdb\uff0cBug \u4fee\u590d\uff0c\u4ee5\u53ca\u5177\u4f53\u7684\u8d21\u732e\u8005\u3002\\n\\n### Apache InLong(incubating) \u540e\u7eed\u89c4\u5212\\n\u540e\u7eed\u7248\u672c\uff0c\u6211\u4eec\u4f1a\u8fdb\u4e00\u6b65\u91ca\u653e InLong \u7684\u80fd\u529b\uff0c\u8986\u76d6\u66f4\u591a\u7684\u4f7f\u7528\u573a\u666f\uff0c\u4e3b\u8981\u5305\u62ec\uff1a\\n- \u652f\u6301\u6570\u636e\u63a5\u5165 ClickHouse \u7684\u94fe\u8def\\n- \u652f\u6301 DB \u6570\u636e\u7684\u91c7\u96c6\\n- \u5168\u94fe\u8def\u7684\u6307\u6807\u5ba1\u8ba1\u4e8c\u671f\u529f\u80fd\\n\\n\\n### Apache InLong(incubating) \u8d21\u732e\u8005\u62db\u52df\\nApache InLong(incubating) \u5f53\u524d\u5171\u6709 71 \u540d contributor\uff0c\u4ecd\u5904\u5728\u9879\u76ee\u5b75\u5316\u7684\u521d\u671f\uff0c\u8fd8\u6709\u5f88\u591a\u5f85\u529e\u4e8b\u9879\uff0c\u5305\u62ec\uff1aFeature \u5f00\u53d1\u3001\u793e\u533a\u8fd0\u8425\uff0c\u6587\u6863\u7ffb\u8bd1\u7b49\uff0c\u671f\u5f85\u66f4\u591a\u5f00\u6e90\u7231\u597d\u8005\u52a0\u5165 InLong\uff0c\u4e00\u8d77\u5c06 InLong \u6253\u9020\u6210 Apache \u9876\u7ea7\u9879\u76ee\u3002\u4ee5\u4e0b\u4e3a InLong \u9879\u76ee\u7684\u65f6\u95f4\u7ebf\uff1a\\n\\n- 2021\u5e7412\u670822\u65e5\uff0c\u53d1\u5e03 0.12.0 \u7248\u672c\\n- 2021\u5e7411\u67085\u65e5\uff0c\u53d1\u5e030.11.0\u7248\u672c\\n- 2021\u5e749\u67083\u65e5\uff0c\u53d1\u5e030.10.0\u7248\u672c\\n- 2021\u5e747\u670812\u65e5\uff0c\u53d1\u8d77\u66f4\u540d\u540e\u7b2c\u4e00\u4e2a\u7248\u672c 0.9.0 \u6295\u7968\\n- 2021\u5e744\u670811\u65e5\uff0c\u5b8c\u6210\u793e\u533a\u6539\u540d\uff0c\u8c03\u6574\u4e3a Apache InLong\\n- 2021\u5e742\u670811\u65e5\uff0c\u53d1\u8d77\u793e\u533a\u6539\u540d\u53d8\u66f4\u7533\u8bf7\\n- 2020\u5e7412\u670820\u65e5\uff0c\u8fdb\u884c\u9879\u76ee\u6539\u540d\u8ba8\u8bba\u548c\u6295\u7968\\n- 2020\u5e745\u670830\u65e5\uff0c\u6309\u7167 Apache \u793e\u533a\u89c4\u8303\u53d1\u5e03\u7b2c\u4e00\u4e2a\u793e\u533a\u7248\u672c\\n- 2019\u5e7411\u67083\u65e5\uff0c\u8fdb\u5165 Apache \u793e\u533a\u5b75\u5316\\n- 2019\u5e749\u670812\u65e5\uff0cTubeMQ \u5bf9\u5916\u5f00\u6e90\u5e76\u6350\u732e\u7ed9 Apache \u793e\u533a"},{"id":"/2021/11/10/release-0.11.0","metadata":{"permalink":"/zh-CN/blog/2021/11/10/release-0.11.0","editUrl":"https://github.com/apache/inlong-website/edit/master/blog/blog/2021-11-10-release-0.11.0.md","source":"@site/i18n/zh-CN/docusaurus-plugin-content-blog/2021-11-10-release-0.11.0.md","title":"0.11.0 \u7248\u672c\u53d1\u5e03","description":"Apache InLong(incubating) \u4ece 0.9.0 \u7248\u672c\u5f00\u59cb\u7531\u539f Apache TubeMQ\uff08incubating\uff09\u6539\u540d\u800c\u6765\uff0c\u4f34\u968f\u7740\u540d\u79f0\u7684\u6539\u53d8\uff0cInLong \u4e5f\u7531\u5355\u4e00\u7684\u6d88\u606f\u961f\u5217\u5347\u7ea7\u4e3a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u652f\u6301\u4e86\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002","date":"2021-11-10T00:00:00.000Z","formattedDate":"2021\u5e7411\u670810\u65e5","tags":[],"readingTime":8.27,"truncated":false,"authors":[],"frontMatter":{"title":"0.11.0 \u7248\u672c\u53d1\u5e03"},"prevItem":{"title":"0.12.0 \u7248\u672c\u53d1\u5e03","permalink":"/zh-CN/blog/2022/01/04/release-0.12.0"}},"content":"Apache InLong(incubating) \u4ece 0.9.0 \u7248\u672c\u5f00\u59cb\u7531\u539f Apache TubeMQ\uff08incubating\uff09\u6539\u540d\u800c\u6765\uff0c\u4f34\u968f\u7740\u540d\u79f0\u7684\u6539\u53d8\uff0cInLong \u4e5f\u7531\u5355\u4e00\u7684\u6d88\u606f\u961f\u5217\u5347\u7ea7\u4e3a\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u652f\u6301\u4e86\u5927\u6570\u636e\u9886\u57df\u7684\u91c7\u96c6\u3001\u6c47\u805a\u3001\u7f13\u5b58\u548c\u5206\u62e3\u529f\u80fd\uff0c\u7528\u6237\u53ea\u9700\u8981\u7b80\u5355\u7684\u914d\u7f6e\u5c31\u53ef\u4ee5\u628a\u6570\u636e\u4ece\u6570\u636e\u6e90\u5bfc\u5165\u5230\u5b9e\u65f6\u8ba1\u7b97\u5f15\u64ce\u6216\u8005\u843d\u5730\u5230\u79bb\u7ebf\u5b58\u50a8\u3002\\n\u521a\u521a\u53d1\u5e03\u7684 0.11.0-incubating \u7248\u672c\u662f\u6539\u540d\u4e4b\u540e\u7684\u7b2c\u4e09\u4e2a\u7248\u672c\uff0c\u8fd9\u4e2a\u7248\u672c\uff1a\\n- \u8fdb\u4e00\u6b65\u964d\u4f4e\u7528\u6237\u7684\u4f7f\u7528\u95e8\u69db\uff0c\u652f\u6301 InLong \u6240\u6709\u6a21\u5757 on Kubernets\uff0c\u5e76\u4e14\u5bf9\u5b98\u7f51\u8fdb\u884c\u4e86\u91cd\u6784\uff0c\u7528\u6237\u53ef\u4ee5\u66f4\u52a0\u65b9\u4fbf\u7684\u67e5\u9605\u76f8\u5173\u6587\u6863\\n- \u652f\u6301\u66f4\u591a\u7684\u4e1a\u52a1\u573a\u666f\uff0c\u589e\u52a0 Dataproxy -> Pulsar \u7684\u6570\u636e\u6d41\u5411\uff0c\u8986\u76d6\u5bf9\u6570\u636e\u5b8c\u6574\u6027\u3001\u4e00\u81f4\u6027\u8981\u6c42\u66f4\u9ad8\u7684\u573a\u666f\\n- \u652f\u6301\u66f4\u591a\u8bed\u8a00\u7684 SDK\uff0c\u8fd9\u4e2a\u7248\u672c\u5f00\u653e\u4e86\u751f\u4ea7\u7ea7\u522b\u7684 TubeMQ Go SDK\uff0c\u65b9\u4fbf\u4f7f\u7528 Go \u8bed\u8a00\u7684\u7528\u6237\u63a5\u5165\\n\\n\u8be5\u7248\u672c\u5173\u95ed\u8d85\u8fc7 80 \u4e2a issue, \u5305\u542b\u4e86\u56db\u4e2a\u91cd\u5927 feature \u548c 35 \u4e2a improvements \u3002\\n\\n### Apache InLong(incubating) \u7b80\u4ecb\\n[Apache InLong\uff08\u5e94\u9f99\uff09](https://inlong.apache.org/zh-cn/)\u662f\u817e\u8baf\u6350\u732e\u7ed9 Apache \u793e\u533a\u7684\u4e00\u7ad9\u5f0f\u6d77\u91cf\u6570\u636e\u96c6\u6210\u6846\u67b6\uff0c\u63d0\u4f9b\u81ea\u52a8\u3001\u5b89\u5168\u3001\u53ef\u9760\u548c\u9ad8\u6027\u80fd\u7684\u6570\u636e\u4f20\u8f93\u80fd\u529b\uff0c\u65b9\u4fbf\u4e1a\u52a1\u6784\u5efa\u57fa\u4e8e\u6d41\u5f0f\u7684\u6570\u636e\u5206\u6790\u3001\u5efa\u6a21\u548c\u5e94\u7528\u3002InLong \u9879\u76ee\u539f\u672c\u53ebTubeMQ \uff0c\u4e13\u6ce8\u9ad8\u6027\u80fd\u3001\u4f4e\u6210\u672c\u7684\u6d88\u606f\u961f\u5217\u670d\u52a1\u3002\u4e3a\u4e86\u8fdb\u4e00\u6b65\u91ca\u653e TubeMQ \u5468\u8fb9\u751f\u6001\u80fd\u529b\uff0c\u6211\u4eec\u5c06\u9879\u76ee\u5347\u7ea7\u4e3a InLong \uff0c\u4e13\u6ce8\u6253\u9020\u4e00\u7ad9\u5f0f\u7684\u6570\u636e\u96c6\u6210\u89e3\u51b3\u65b9\u6848\u3002\\n\\nApache InLong \u4ee5\u817e\u8baf\u5185\u90e8\u4f7f\u7528\u7684 TDBank \u4e3a\u539f\u578b\uff0c\u4f9d\u6258\u4e07\u4ebf\u7ea7\u522b\u7684\u6570\u636e\u63a5\u5165\u548c\u5904\u7406\u80fd\u529b\uff0c\u6574\u5408\u4e86\u6570\u636e\u91c7\u96c6\u3001\u6c47\u805a\u3001\u5b58\u50a8\u3001\u5206\u62e3\u6570\u636e\u5904\u7406\u5168\u6d41\u7a0b\uff0c\u62e5\u6709\u7b80\u5355\u6613\u7528\u3001\u7075\u6d3b\u6269\u5c55\u3001\u7a33\u5b9a\u53ef\u9760\u7b49\u7279\u6027\u3002\\n<img src=\\"../img/inlong-structure-zh.png\\" align=\\"center\\" alt=\\"Apache InLong\\"/>\\n\\n Apache InLong \u670d\u52a1\u4e8e\u6570\u636e\u91c7\u96c6\u5230\u843d\u5730\u7684\u6574\u4e2a\u751f\u547d\u5468\u671f\uff0c\u6309\u6570\u636e\u7684\u4e0d\u540c\u9636\u6bb5\u63d0\u4f9b\u4e0d\u540c\u7684\u5904\u7406\u6a21\u5757\uff0c\u4e3b\u8981\u5305\u62ec\uff1a\\n - **inlong-agent** \uff0c\u6570\u636e\u91c7\u96c6 Agent \uff0c\u652f\u6301\u4ece\u6307\u5b9a\u76ee\u5f55\u6216\u6587\u4ef6\u8bfb\u53d6\u5e38\u89c4\u65e5\u5fd7\uff0c\u8fdb\u884c\u9010\u6761\u7684\u6570\u636e\u4e0a\u62a5\u3002\u540e\u7eed\u4e5f\u5c06\u6269\u5c55 DB \u91c7\u96c6\uff0c\u6269\u5c55HTTP\u4e0a\u62a5\u7b49\u80fd\u529b\u3002\\n - **inlong-dataproxy** \uff0c\u4e00\u4e2a\u57fa\u4e8e Flume-ng \u7684 Proxy \u7ec4\u4ef6\uff0c\u652f\u6301\u6570\u636e\u53d1\u9001\u963b\u585e\u3001\u843d\u76d8\u91cd\u53d1\uff0c\u62e5\u6709\u5c06\u63a5\u6536\u6570\u636e\u540e\u8f6c\u53d1\u5230\u4e0d\u540cMQ\uff08\u6d88\u606f\u961f\u5217\uff09\u7684\u80fd\u529b\u3002\\n - **inlong-tubemq** \uff0c\u817e\u8baf\u81ea\u7814\u7684\u6d88\u606f\u961f\u5217\u670d\u52a1\uff0c\u4e13\u6ce8\u670d\u52a1\u5927\u6570\u636e\u573a\u666f\u4e0b\u6d77\u91cf\u6570\u636e\u7684\u9ad8\u6027\u80fd\u5b58\u50a8\u548c\u4f20\u8f93\uff0c\u5728\u6d77\u91cf\u5b9e\u8df5\u548c\u4f4e\u6210\u672c\u65b9\u9762\u6709\u7740\u6bd4\u8f83\u597d\u7684\u6838\u5fc3\u4f18\u52bf\u3002 \u6ce8\uff1aInLong 0.11.0 \u540e\u53f0\u4e2d\u589e\u52a0\u4e86\u5bf9\u4e8eApache Pulsar\u7684\u652f\u6301\uff0c\u5168\u6d41\u7a0b\u6570\u636e\u6d41\u548c\u7ba1\u63a7\u5de5\u4f5c\u4f1a\u5728\u4e0b\u4e2a\u7248\u672c\u5b8c\u6210\u3002\\n - **inlong-sort** \uff0c\u4ece\u4e0d\u540c\u7684 MQ \u6d88\u8d39\u6570\u636e\u540e\u8fdb\u884c ETL \u5904\u7406\uff0c\u7136\u540e\u5c06\u6570\u636e\u6c47\u805a\u5e76\u5199\u5165 Hive\u3001ClickHouse\u3001Hbase\u3001IceBerg \u7b49\u3002\\n - **inlong-manager** \uff0c\u63d0\u4f9b\u5b8c\u6574\u7684\u6570\u636e\u670d\u52a1\u7ba1\u63a7\u80fd\u529b\uff0c\u5305\u62ec\u5143\u6570\u636e\u3001OpenAPI\u3001\u4efb\u52a1\u6d41\u3001\u6743\u9650\u7b49\u3002\\n - **inlong-website** \uff0c\u4e00\u4e2a\u7528\u4e8e\u7ba1\u7406\u6570\u636e\u63a5\u5165\u7684\u524d\u7aef\u9875\u9762\uff0c\u7b80\u5316\u6574\u4e2a InLong \u7ba1\u63a7\u5e73\u53f0\u7684\u4f7f\u7528\u3002\\n\\n### Apache InLong(incubating) 0.11.0 \u4e3b\u8981\u7279\u6027\\n#### InLong on Kubernetes \\nInLong \u5305\u62ec\u4e86\u6570\u636e\u91c7\u96c6\uff0c\u6570\u636e\u6c47\u805a\uff0c\u6570\u636e\u7f13\u5b58\u3001\u6570\u636e\u5206\u62e3\u4ee5\u53ca\u96c6\u7fa4\u7ba1\u63a7\u7b49\u591a\u4e2a\u7ec4\u4ef6\uff0c\u4e3a\u4e86\u65b9\u4fbf\u7528\u6237\u4f7f\u7528\u548c\u652f\u6301\u4e91\u539f\u751f\u7279\u6027\uff0c\u76ee\u524d\u6240\u6709\u7684\u7ec4\u4ef6\u90fd\u5df2\u7ecf\u652f\u6301\u5728 Kubernetes \u90e8\u7f72\u3002\\n\u611f\u8c22 @shink \u8d21\u732e\u7684\u8fd9\u4e2a\u7279\u6027\uff0c\u5177\u4f53\u8be6\u60c5\u53ef\u4ee5\u53c2\u8003:\\n[INLONG-1308](https://github.com/apache/incubator-inlong/issues/1308)\\n\\n#### dataproxy->pulsar \u6570\u636e\u6d41\u6253\u901a\\n\u5728 0.11.0 \u7248\u672c\u4e4b\u524d\u7684\u7248\u672c\uff0cInLong \u7684\u6570\u636e\u7f13\u5b58\u5c42\u53ea\u80fd\u652f\u6301 TubeMQ\uff0cTubeMQ \u5f88\u9002\u5408\u4e8e\u8d85\u5927\u89c4\u6a21\u6570\u636e\u91cf\u7684\u573a\u666f\uff0c\u4f46\u5728\u6781\u7aef\u573a\u666f\u4e0b\u53ef\u80fd\u4f1a\u6709\u5c11\u91cf\u6570\u636e\u4e22\u5931\u7684\u98ce\u5411\uff1b\u4e3a\u4e86\u63d0\u4f9b\u6570\u636e\u53ef\u9760\u6027\uff0cInlong \u5728 0.11.0 \u7248\u672c\u4e2d\u589e\u52a0\u4e86\u5bf9\u4e8e Apache Pulsar \u7684\u652f\u6301\uff0c\u73b0\u5728InLong \u540e\u53f0\u53ef\u4ee5\u652f\u6301\u6570\u636e\u6d41\u53ef\u4ee5\u4ece agent -> dataProxy -> tubeMQ/pulsar -> sort.  Pulsar \u7684\u5f15\u5165\uff0c\u4f7f\u5f97 InLong \u8986\u76d6\u7684\u5e94\u7528\u573a\u666f\u66f4\u52a0\u4e30\u5bcc\uff0c\u53ef\u4ee5\u6ee1\u8db3\u66f4\u591a\u7528\u6237\u7684\u9700\u6c42\u3002\\n\u611f\u8c22 @baomingyu \u5bf9\u4e8e\u8fd9\u4e2a\u7279\u6027\u7684\u8d21\u732e\uff0c\u66f4\u591a\u8be6\u60c5\u53ef\u4ee5\u53c2\u8003:\\n[INLONG-1330](https://github.com/apache/incubator-inlong/issues/1330)\\n\\n#### Go \u8bed\u8a00 SDK\\n\u5728 0.11.0 \u7248\u672c\u4e4b\u524d\u7684 TubeMQ \u652f\u6301 Java \u3001C++\u3001 Python \u4e09\u79cd\u8bed\u8a00\u7684 SDK\uff0c\u968f\u7740 Go \u8bed\u8a00\u7684\u5e94\u7528\u8d8a\u6765\u8d8a\u591a\uff0c\u793e\u533a\u4e2d\u5bf9\u4e8e Go \u8bed\u8a00 SDK \u7684\u9700\u6c42\u4e5f\u65e5\u76ca\u589e\u52a0\uff0c0.11.0 \u7248\u672c\u6b63\u5f0f\u5f15\u5165\u4e86 TubeMQ \u7684 Go \u8bed\u8a00 SDK\u3002\u4e30\u5bcc\u4e86\u591a\u8bed\u8a00 SDK\uff0c\u4e5f\u964d\u4f4e\u4e86 Go \u8bed\u8a00\u7528\u6237\u7684\u63a5\u5165\u548c\u4f7f\u7528\u96be\u5ea6\u3002\\n\u611f\u8c22 @TszKitLo40 \u8d21\u732e\u7684\u8fd9\u4e2a\u7279\u6027\uff0c\u66f4\u591a\u8be6\u60c5\u53ef\u4ee5\u53c2\u8003\uff1a\\n[INLONG-624](https://github.com/apache/incubator-inlong/issues/624)\\n[INLONG-1578](https://github.com/apache/incubator-inlong/issues/1570)\\n[INLONG-1584](https://github.com/apache/incubator-inlong/issues/1584)\\n[INLONG-1666](https://github.com/apache/incubator-inlong/issues/1666)\\n[INLONG-1682](https://github.com/apache/incubator-inlong/issues/1682)\\n\\n#### \u5b98\u7f51\u91cd\u6784\\n\u5728 0.11.0 \u7248\u672c\u4e2d\uff0cInLong \u91c7\u7528 Docusaurus \u91cd\u6784\u4e86[\u5b98\u7f51](https://inlong.apache.org/)\uff0c\u63d0\u4f9b\u4e86\u66f4\u52a0\u7b80\u6d01\u3001\u76f4\u89c2\u7684\u6587\u6863\u7ba1\u7406\u548c\u5c55\u793a\u3002\\n\u611f\u8c22 @leezng \u5bf9\u4e8e\u8fd9\u4e2a\u7279\u6027\u7684\u8d21\u732e\u3002\u66f4\u591a\u8be6\u60c5\u53ef\u4ee5\u53c2\u8003\uff1a\\n[INLONG-1631](https://github.com/apache/incubator-inlong/issues/1631)\\n[INLONG-1632](https://github.com/apache/incubator-inlong/issues/1632)\\n[INLONG-1633](https://github.com/apache/incubator-inlong/issues/1633)\\n[INLONG-1634](https://github.com/apache/incubator-inlong/issues/1634)\\n[INLONG-1635](https://github.com/apache/incubator-inlong/issues/1635)\\n[INLONG-1636](https://github.com/apache/incubator-inlong/issues/1636)\\n[INLONG-1637](https://github.com/apache/incubator-inlong/issues/1637)\\n[INLONG-1638](https://github.com/apache/incubator-inlong/issues/1638)\\n\\n\u9664\u4e86\u4e0a\u8ff0\u91cd\u5927 feature \u4e4b\u5916\uff0cInLong 0.11.0 \u7248\u672c\u8fd8\u6709\u5176\u4ed6\u7684\u5173\u952e\u6539\u8fdb\uff0c\u5305\u62ec\u4f46\u4e0d\u9650\u4e8e\uff1a\\n- \u5728\u4e3b Repo \u4e2d\u589e\u52a0\u4e86\u8d21\u732e\u6307\u5f15\uff0c[INLONG-1623](https://github.com/apache/incubator-inlong/issues/1623)\\n- \u589e\u52a0 Inlong-Manager \u5bf9 DataProxy \u7684\u914d\u7f6e\u7ba1\u7406\uff0c[INLONG-1595](https://github.com/apache/incubator-inlong/issues/1595)\\n- \u4f18\u5316\u4e86 github issue \u6a21\u677f\uff0c[INLONG-1585](https://github.com/apache/incubator-inlong/issues/1585)\\n- \u4ee3\u7801 Checkstyle \u4f18\u5316\uff0c[INLONG-1571](https://github.com/apache/incubator-inlong/issues/1571), [INLONG-1593](https://github.com/apache/incubator-inlong/issues/1593), [INLONG-1593](https://github.com/apache/incubator-inlong/issues/1593), [INLONG-1662](https://github.com/apache/incubator-inlong/issues/1662)\\n- Agent \u5f15\u5165\u6d88\u606f\u8fc7\u6ee4\u5668\uff0c[INLONG-1641](https://github.com/apache/incubator-inlong/issues/1641)\\n\\n0.11.0 \u7248\u672c\u8fd8\u4fee\u590d\u4e86~45\u4e2abug\uff0c\u5728\u6b64\uff0c\u611f\u8c22\u6240\u6709\u4e3a Inlong \u793e\u533a\u505a\u51fa\u8d21\u732e\u7684\u5404\u4f4d\u540c\u5b66\uff08\u6392\u540d\u4e0d\u5206\u5148\u540e\uff09\\nshink, baomingyu, TszKitLo40, leezng, ruanwenjun, leo65535, luchunliang, pierre94, EMsnap, dockerzhang, gosonzhang, healchow, guangxuCheng, yuanboliu\\n\\n\\n### Apache InLong(incubating) \u540e\u7eed\u89c4\u5212\\n\u5728 InLong \u540e\u7eed\u7248\u672c\u89c4\u5212\u4e2d\uff0c\u6211\u4eec\u4f1a\u8fdb\u4e00\u6b65\u91ca\u653e InLong \u7684\u80fd\u529b\uff0c\u8986\u76d6\u66f4\u591a\u7684\u4f7f\u7528\u573a\u666f\uff0c\u4e3b\u8981\u5305\u62ec\\n\\n- \u652f\u6301 Apache Pulsar \u5168\u94fe\u8def\u6570\u636e\u63a5\u5165\u80fd\u529b\uff0c\u5305\u62ec\u540e\u7aef\u5904\u7406\u548c\u524d\u7aef\u7ba1\u7406\u80fd\u529b\u3002\\n- \u652f\u6301 ClickHouse\u3001Apache Iceberg\u3001Apache HBase \u7b49\u6570\u636e\u6d41\u5411\\n\\n### Apache InLong(incubating) \u8d21\u732e\u8005\u62db\u52df\\nApache InLong(incubating) \u5f53\u524d\u7684 contributor \u5171\u8ba162\u540d\uff0c\u8fd8\u5904\u5728\u9879\u76ee\u5b75\u5316\u7684\u521d\u671f\uff0c\u6709\u5f88\u591a\u5de5\u4f5c\u9700\u8981\u505a\uff0c\u5305\u62ec\u793e\u533a\u8fd0\u8425\u3001\u6587\u6863\u7ffb\u8bd1\u3001Feature \u5f00\u53d1\u7b49\uff0c\u671f\u5f85\u66f4\u591a\u7684\u5f00\u6e90\u7231\u597d\u8005\u4e00\u8d77\u5171\u5efa\uff0c\u52aa\u529b\u5c06 InLong \u6253\u9020\u6210 Apache \u9876\u7ea7\u9879\u76ee\uff0c\u4ee5\u4e0b\u4e3a InLong \u91cd\u8981\u53d1\u5c55\u65f6\u95f4\u70b9\uff1a\\n- 2021\u5e7411\u67085\u65e5\uff0c\u53d1\u5e030.11.0\u7248\u672c\\n- 2021\u5e749\u67083\u65e5\uff0c\u53d1\u5e030.10.0\u7248\u672c\\n- 2021\u5e747\u670812\u65e5\uff0c\u53d1\u8d77\u66f4\u540d\u540e\u7b2c\u4e00\u4e2a\u7248\u672c 0.9.0 \u6295\u7968\\n- 2021\u5e744\u670811\u65e5\uff0c\u5b8c\u6210\u793e\u533a\u6539\u540d\uff0c\u8c03\u6574\u4e3a Apache InLong\\n- 2021\u5e742\u670811\u65e5\uff0c\u53d1\u8d77\u793e\u533a\u6539\u540d\u53d8\u66f4\u7533\u8bf7\\n- 2020\u5e7412\u670820\u65e5\uff0c\u8fdb\u884c\u9879\u76ee\u6539\u540d\u8ba8\u8bba\u548c\u6295\u7968\\n- 2020\u5e745\u670830\u65e5\uff0c\u6309\u7167 Apache \u793e\u533a\u89c4\u8303\u53d1\u5e03\u7b2c\u4e00\u4e2a\u793e\u533a\u7248\u672c\\n- 2019\u5e7411\u67083\u65e5\uff0c\u8fdb\u5165 Apache \u793e\u533a\u5b75\u5316\\n- 2019\u5e749\u670812\u65e5\uff0cTubeMQ \u5bf9\u5916\u5f00\u6e90\u5e76\u6350\u732e\u7ed9 Apache \u793e\u533a"}]}')}}]);